  1145→
  1146→    try {
  1147→        const response = await fetchWithTimeout(
  1148→            `${getBackendUrl()}/license/activate`,
  1149→            {
  1150→                method: 'POST',
  1151→                headers: { 'Content-Type': 'application/json' },
  1152→                body: JSON.stringify({ key: licenseKey })
  1153→            }
  1154→        );
  1155→
  1156→        if (!response.ok) {
  1157→            throw new Error(await parseErrorResponse(response));
  1158→        }
  1159→
  1160→        const data = await response.json();
  1161→        saveSettings({
  1162→            customerId: data.customerId,
  1163→            licenseKey: licenseKey
  1164→        });
  1165→
  1166→        if (ui.loginModal) ui.loginModal.classList.add('hidden');
  1167→        setStatus('License activated');
