  1050→
  1051→function buildSequenceFromCutList(cutListJson) {
  1052→    try {
  1053→        var cutList = JSON.parse(cutListJson);
  1054→        var seq = app.project.activeSequence;
  1055→        if (!seq) return JSON.stringify({ error: "No active sequence" });
  1056→
  1057→        // Clone the sequence
  1058→        var newSequenceName = seq.name + "_SPLICE";
  1059→        seq.clone();
  1060→
  1061→        // Find the cloned sequence
  1062→        var newSeq = null;
  1063→        for (var i = app.project.sequences.numSequences - 1; i >= 0; i--) {
  1064→            var s = app.project.sequences[i];
  1065→            if (s.name.indexOf(seq.name) === 0 && s.sequenceID !== seq.sequenceID) {
  1066→                newSeq = s;
  1067→                break;
  1068→            }
  1069→        }
  1070→
  1071→        if (!newSeq) {
  1072→            return JSON.stringify({ error: "Failed to clone sequence" });
  1073→        }
  1074→
  1075→        newSeq.name = newSequenceName;
  1076→        app.project.activeSequence = newSeq;
  1077→
  1078→        // Clear all clips from the new sequence
  1079→        clearSequence();
  1080→
  1081→        // Insert segments
  1082→        var currentPosition = 0;
  1083→        var stats = {
  1084→            segmentsInserted: 0,
  1085→            totalDuration: 0
  1086→        };
  1087→
  1088→        for (var i = 0; i < cutList.segments.length; i++) {
  1089→            var segment = cutList.segments[i];
  1090→
  1091→            // Find the source project item
  1092→            var sourceItem = null;
  1093→            if (segment.sourcePath) {
  1094→                sourceItem = findItemByPath(app.project.rootItem, segment.sourcePath);
  1095→            }
  1096→            if (!sourceItem && segment.sourceName) {
  1097→                sourceItem = findItemByName(app.project.rootItem, segment.sourceName);
  1098→            }
  1099→
  1100→            if (!sourceItem) continue;
  1101→
  1102→            // Set color label
  1103→            if (segment.colorHint && SEGMENT_COLORS[segment.colorHint]) {
  1104→                sourceItem.setColorLabel(SEGMENT_COLORS[segment.colorHint]);
  1105→            } else if (segment.type && SEGMENT_COLORS[segment.type]) {
  1106→                sourceItem.setColorLabel(SEGMENT_COLORS[segment.type]);
  1107→            }
  1108→
  1109→            // Insert clip at current position
  1110→            var targetTime = new Time();
  1111→            targetTime.seconds = currentPosition;
  1112→
  1113→            newSeq.videoTracks[0].insertClip(sourceItem, targetTime);
  1114→
  1115→            // Set in/out points
  1116→            var clipDuration = segment.outPoint - segment.inPoint;
  1117→            var videoClips = newSeq.videoTracks[0].clips;
  1118→            var insertedClip = videoClips[videoClips.numItems - 1];
  1119→
  1120→            if (insertedClip) {
  1121→                var inTime = new Time();
  1122→                inTime.seconds = segment.inPoint;
  1123→                insertedClip.inPoint = inTime;
  1124→
  1125→                var outTime = new Time();
  1126→                outTime.seconds = segment.outPoint;
  1127→                insertedClip.outPoint = outTime;
  1128→
  1129→                // Set clip name if takeLabel provided
  1130→                if (segment.takeLabel) {
  1131→                    insertedClip.name = segment.takeLabel;
  1132→                }
  1133→            }
  1134→
  1135→            currentPosition += clipDuration;
  1136→            stats.segmentsInserted++;
  1137→            stats.totalDuration += clipDuration;
  1138→        }
  1139→
  1140→        return JSON.stringify({
  1141→            success: true,
  1142→            sequenceName: newSequenceName,
  1143→            stats: stats
  1144→        });
  1145→    } catch (e) {
  1146→        return JSON.stringify({ error: e.message });
  1147→    }
  1148→}
