   336→ * @returns {string} Escaped string safe for innerHTML
   337→ */
   338→function escapeHtml(str) {
   339→    if (str === null || str === undefined) return '';
   340→    if (typeof str !== 'string') str = String(str);
   341→
   342→    const escapeMap = {
   343→        '&': '&amp;',
   344→        '<': '&lt;',
   345→        '>': '&gt;',
   346→        '"': '&quot;',
   347→        "'": '&#x27;',
   348→        '/': '&#x2F;',
