  1027→    try {
  1028→        for (const chapter of currentChapters) {
  1029→            await jsx.call('addChapterMarker', chapter.startTime, chapter.title, chapter.description);
  1030→        }
  1031→        setStatus(`Added ${currentChapters.length} chapter markers`);
  1032→    } catch (error) {
  1033→        setStatus('Failed to add markers: ' + error.message, true);
  1034→    } finally {
  1035→        isOperationInProgress = false;
  1036→    }
  1037→}
  1038→
  1039→/**
  1040→ * Apply zoom point markers to the timeline
  1041→ */
  1042→async function applyZoomMarkers() {
  1043→    if (isOperationInProgress || currentZooms.length === 0) return;
  1044→
  1045→    isOperationInProgress = true;
  1046→    setStatus('Adding zoom markers...');
  1047→
  1048→    try {
  1049→        for (const zoom of currentZooms) {
  1050→            // Create a marker for each zoom point with zoom intensity info
  1051→            const comment = `Zoom ${zoom.intensity || 'medium'} - ${zoom.reason || 'emphasis'}`;
  1052→            await jsx.call('createMarker', zoom.time, 'SPLICE: Zoom', 0.5, comment, 3);
  1053→        }
  1054→        setStatus(`Added ${currentZooms.length} zoom markers`);
  1055→    } catch (error) {
