  1080→            ui.settingsModal.classList.add('hidden');
  1081→        });
  1082→    }
  1083→
  1084→    // Remember options checkbox
  1085→    const rememberOptions = document.getElementById('rememberOptions');
  1086→    if (rememberOptions) {
  1087→        rememberOptions.addEventListener('change', () => {
  1088→            saveSettings({ rememberOptions: rememberOptions.checked });
  1089→            if (rememberOptions.checked) {
  1090→                setStatus('Options will be remembered');
  1091→            } else {
  1092→                setStatus('Options will reset to defaults');
  1093→            }
  1094→        });
  1095→    }
  1096→
  1097→    // Login modal
  1098→    if (ui.creditBadge) {
  1099→        ui.creditBadge.addEventListener('click', () => {
  1100→            const settings = getSettings();
  1101→            if (!settings.customerId) {
  1102→                showLoginModal();
  1103→            }
  1104→        });
  1105→    }
  1106→
  1107→    if (ui.closeLoginBtn && ui.loginModal) {
  1108→        ui.closeLoginBtn.addEventListener('click', () => {
  1109→            ui.loginModal.classList.add('hidden');
  1110→        });
  1111→    }
  1112→
  1113→    if (ui.saveLoginBtn) {
  1114→        ui.saveLoginBtn.addEventListener('click', activateLicense);
  1115→    }
  1116→
  1117→    // License lookup button
  1118→    const lookupLicenseBtn = document.getElementById('lookupLicenseBtn');
  1119→    if (lookupLicenseBtn) {
