   150→/**
   151→ * Get the actual frames per second from the sequence
   152→ * videoFrameRate.seconds gives seconds per frame, so we need to invert it
   153→ */
   154→function getActualFrameRate() {
   155→    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
   156→    // secsPerFrame is e.g., 0.03333... for 30fps, so 1/secsPerFrame = 30
   157→    return 1 / secsPerFrame;
   158→}
   159→
   160→function quantise_time(time_seconds) {
   161→    // Get seconds per frame (e.g., 0.0333 for 30fps)
   162→    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
   163→    // Round to nearest frame boundary
   164→    return secsPerFrame * Math.round(time_seconds / secsPerFrame);
   165→}
   166→
   167→function frames_to_time(number_of_frames) {
   168→    // seconds per frame * number of frames = total seconds
   169→    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
   170→    return number_of_frames * secsPerFrame;
   171→}
   172→
   173→function frames_to_ticks(number_of_frames) {
   174→    // Convert frames to ticks: frames * seconds_per_frame * ticks_per_second
   175→    var secsPerFrame = app.project.activeSequence.getSettings().videoFrameRate.seconds;
   176→    return number_of_frames * secsPerFrame * TICKS_PER_SECOND;
   177→}
   178→
   179→function seconds_to_ticks(seconds) {
   180→    return seconds * TICKS_PER_SECOND;
   181→}
   182→
   183→function ticks_to_seconds(ticks) {
   184→    return ticks / TICKS_PER_SECOND;
