   122→            if (arg === null || arg === undefined) return 'null';
   123→            if (typeof arg === 'string') return `'${arg.replace(/'/g, "\\'").replace(/\n/g, '\\n')}'`;
   124→            if (typeof arg === 'object') return `'${JSON.stringify(arg).replace(/'/g, "\\'")}'`;
   125→            return String(arg);
   126→        });
