/**
 * SPLICE CEP Panel Styles
 * Premium "Deep Space" Theme
 */

:root {
    /* Palette */
    --bg-core: #121212;
    --bg-panel: var(--bg-panel);
    --bg-card: rgba(30, 30, 30, 0.6);
    --glass-border: rgba(255, 255, 255, 0.08);
    --glass-highlight: rgba(255, 255, 255, 0.03);

    /* Accents */
    --accent-primary: var(--accent-primary);
    --accent-gradient: linear-gradient(135deg, var(--accent-primary), #a64dff);
    --accent-glow: 0 0 20px rgba(74, 158, 255, 0.15);

    /* Functional Colors */
    --text-main: #f0f0f0;
    --text-muted: var(--text-muted)888;
    --success: #00e676;
    --error: #ff5252;

    /* Spacing & Radius */
    --radius-sm: 6px;
    --radius-md: 12px;
    --radius-lg: 16px;
    --spacing-xs: 4px;
    --spacing-sm: 8px;
    --spacing-md: 16px;

    /* Transitions */
    --ease-smooth: cubic-bezier(0.4, 0.0, 0.2, 1);
}

/* Reset & Base */
* {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
    background: var(--bg-core);
    color: var(--text-main);
    margin: 0;
    padding: var(--spacing-md);
    height: 100vh;

