   198→/**
   199→ * Reserve credits atomically using SELECT ... FOR UPDATE
   200→ * This prevents race conditions where multiple requests pass credit check
   201→ * before any deduction occurs.
   202→ *
   203→ * @param {string} stripeCustomerId - Customer ID
   204→ * @param {number} estimatedSeconds - Estimated processing time in seconds
   205→ * @param {string} endpoint - Endpoint name for tracking
   206→ * @returns {Promise<{success: boolean, reservationId?: string, error?: string}>}
   207→ */
   208→async function reserveCredits(stripeCustomerId, estimatedSeconds = 0, endpoint = 'unknown') {
   209→  const estimatedHours = estimatedSeconds / 3600;
   210→  const client = await pool.connect();
   211→
   212→  try {
   213→    await client.query('BEGIN');
   214→
   215→    // Lock the row for this customer - prevents concurrent modifications
   216→    const result = await client.query(
   217→      `SELECT id, hours_remaining FROM users
   218→       WHERE stripe_customer_id = $1
   219→       FOR UPDATE`,
   220→      [stripeCustomerId]
   221→    );
   222→
   223→    if (result.rows.length === 0) {
   224→      // Create user if doesn't exist (within transaction)
   225→      const newUser = await client.query(
   226→        `INSERT INTO users (stripe_customer_id, tier, hours_remaining, hours_total)
   227→         VALUES ($1, 'starter', $2, $2)
   228→         RETURNING id, hours_remaining`,
   229→        [stripeCustomerId, TIER_HOURS.starter]
   230→      );
   231→      result.rows = newUser.rows;
   232→    }
   233→
   234→    const user = result.rows[0];
   235→    const hoursRemaining = parseFloat(user.hours_remaining);
   236→
   237→    // Check if enough credits
   238→    if (hoursRemaining < estimatedHours) {
   239→      await client.query('ROLLBACK');
   240→      return {
   241→        success: false,
   242→        error: 'Insufficient credits',
   243→        hoursRemaining,
   244→        hoursRequired: estimatedHours
   245→      };
   246→    }
   247→
   248→    // Generate unique reservation ID
   249→    const reservationId = `res_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
   250→
   251→    // Reserve credits by deducting the estimated amount
   252→    await client.query(
   253→      `UPDATE users
   254→       SET hours_remaining = hours_remaining - $1,
   255→           updated_at = CURRENT_TIMESTAMP
   256→       WHERE stripe_customer_id = $2`,
   257→      [estimatedHours, stripeCustomerId]
   258→    );
   259→
   260→    // Log the reservation
   261→    await client.query(
   262→      `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   263→       VALUES ($1, $2, $3, $4, $5)`,
   264→      [user.id, stripeCustomerId, estimatedSeconds, estimatedHours, `${endpoint}:reserved`]
   265→    );
   266→
   267→    await client.query('COMMIT');
   268→
   269→    console.log(`[UsageTracking] Reserved ${estimatedHours.toFixed(4)}hrs for ${stripeCustomerId} (${reservationId})`);
   270→
   271→    return {
   272→      success: true,
   273→      reservationId,
   274→      hoursReserved: estimatedHours,
   275→      hoursRemaining: hoursRemaining - estimatedHours
   276→    };
   277→  } catch (err) {
   278→    await client.query('ROLLBACK');
   279→    console.error('[UsageTracking] Reservation error:', err);
   280→    throw err;
   281→  } finally {
   282→    client.release();
   283→  }
   284→}
   285→
   286→/**
   287→ * Confirm a reservation (finalize usage) or adjust for actual duration
   288→ *
   289→ * @param {string} stripeCustomerId - Customer ID
   290→ * @param {number} actualSeconds - Actual processing time in seconds
   291→ * @param {number} reservedSeconds - Originally reserved seconds
   292→ * @param {string} endpoint - Endpoint name
   293→ * @returns {Promise<object>} Updated balance
   294→ */
   295→async function confirmReservation(stripeCustomerId, actualSeconds, reservedSeconds, endpoint = 'unknown') {
   296→  const actualHours = actualSeconds / 3600;
   297→  const reservedHours = reservedSeconds / 3600;
   298→  const difference = reservedHours - actualHours;
   299→
   300→  const client = await pool.connect();
   301→  try {
   302→    await client.query('BEGIN');
   303→
   304→    // Lock the row
   305→    const result = await client.query(
   306→      `SELECT id, hours_remaining FROM users
   307→       WHERE stripe_customer_id = $1
   308→       FOR UPDATE`,
   309→      [stripeCustomerId]
   310→    );
   311→
   312→    if (result.rows.length === 0) {
   313→      await client.query('ROLLBACK');
   314→      throw new Error('User not found');
   315→    }
   316→
   317→    const user = result.rows[0];
   318→
   319→    // If actual usage was less than reserved, refund the difference
   320→    if (difference > 0) {
   321→      await client.query(
   322→        `UPDATE users
   323→         SET hours_remaining = hours_remaining + $1,
   324→             updated_at = CURRENT_TIMESTAMP
   325→         WHERE stripe_customer_id = $2`,
   326→        [difference, stripeCustomerId]
   327→      );
   328→
   329→      // Log the adjustment
   330→      await client.query(
   331→        `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   332→         VALUES ($1, $2, $3, $4, $5)`,
   333→        [user.id, stripeCustomerId, -(reservedSeconds - actualSeconds), -difference, `${endpoint}:adjusted`]
   334→      );
   335→    } else if (difference < 0) {
   336→      // Actual usage was more than reserved - deduct the extra
   337→      await client.query(
   338→        `UPDATE users
   339→         SET hours_remaining = GREATEST(0, hours_remaining - $1),
   340→             updated_at = CURRENT_TIMESTAMP
   341→         WHERE stripe_customer_id = $2`,
   342→        [-difference, stripeCustomerId]
   343→      );
   344→
   345→      // Log the additional usage
   346→      await client.query(
   347→        `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   348→         VALUES ($1, $2, $3, $4, $5)`,
   349→        [user.id, stripeCustomerId, actualSeconds - reservedSeconds, -difference, `${endpoint}:additional`]
   350→      );
   351→    }
   352→
   353→    await client.query('COMMIT');
   354→
   355→    console.log(`[UsageTracking] Confirmed ${actualHours.toFixed(4)}hrs (reserved: ${reservedHours.toFixed(4)}) for ${stripeCustomerId}`);
   356→
   357→    return await getBalance(stripeCustomerId);
   358→  } catch (err) {
   359→    await client.query('ROLLBACK');
   360→    throw err;
   361→  } finally {
   362→    client.release();
   363→  }
   364→}
   365→
   366→/**
   367→ * Release a reservation (refund credits on processing failure)
   368→ *
   369→ * @param {string} stripeCustomerId - Customer ID
   370→ * @param {number} reservedSeconds - Originally reserved seconds
   371→ * @param {string} endpoint - Endpoint name
   372→ * @param {string} reason - Reason for release
   373→ * @returns {Promise<object>} Updated balance
   374→ */
   375→async function releaseReservation(stripeCustomerId, reservedSeconds, endpoint = 'unknown', reason = 'failed') {
   376→  const reservedHours = reservedSeconds / 3600;
   377→
   378→  const client = await pool.connect();
   379→  try {
   380→    await client.query('BEGIN');
   381→
   382→    // Lock the row
   383→    const result = await client.query(
   384→      `SELECT id FROM users
   385→       WHERE stripe_customer_id = $1
   386→       FOR UPDATE`,
   387→      [stripeCustomerId]
   388→    );
   389→
   390→    if (result.rows.length === 0) {
   391→      await client.query('ROLLBACK');
   392→      throw new Error('User not found');
   393→    }
   394→
   395→    const user = result.rows[0];
   396→
   397→    // Refund the reserved amount
   398→    await client.query(
   399→      `UPDATE users
   400→       SET hours_remaining = hours_remaining + $1,
   401→           updated_at = CURRENT_TIMESTAMP
   402→       WHERE stripe_customer_id = $2`,
   403→      [reservedHours, stripeCustomerId]
   404→    );
   405→
   406→    // Log the refund
   407→    await client.query(
   408→      `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   409→       VALUES ($1, $2, $3, $4, $5)`,
   410→      [user.id, stripeCustomerId, -reservedSeconds, -reservedHours, `${endpoint}:released:${reason}`]
   411→    );
   412→
   413→    await client.query('COMMIT');
   414→
   415→    console.log(`[UsageTracking] Released ${reservedHours.toFixed(4)}hrs for ${stripeCustomerId} (${reason})`);
   416→
   417→    return await getBalance(stripeCustomerId);
   418→  } catch (err) {
   419→    await client.query('ROLLBACK');
   420→    throw err;
   421→  } finally {
   422→    client.release();
   423→  }
   424→}
