   426→/**
   427→ * Check if user can use vocal isolation
   428→ * Returns: { allowed, hasIncludedHours, overageRequired, overageCost }
   429→ */
   430→async function checkIsolationAccess(stripeCustomerId, estimatedMinutes = 0) {
   431→  const balance = await getBalance(stripeCustomerId);
   432→
   433→  // Starter tier has no isolation access
   434→  if (!balance.hasIsolationAccess) {
   435→    return {
   436→      allowed: false,
   437→      reason: 'upgrade_required',
   438→      message: 'Vocal isolation requires Pro or Team tier',
   439→      hasIncludedHours: false,
   440→      overageRequired: false,
   441→      overageCost: 0
   442→    };
   443→  }
   444→
   445→  const estimatedHours = estimatedMinutes / 60;
   446→  const remainingIncluded = balance.isolationHoursRemaining;
   447→
   448→  // Check if within included hours
   449→  if (remainingIncluded >= estimatedHours) {
   450→    return {
   451→      allowed: true,
   452→      reason: 'included',
   453→      message: `Using ${estimatedMinutes.toFixed(1)} min of included isolation time`,
   454→      hasIncludedHours: true,
   455→      overageRequired: false,
   456→      overageCost: 0,
   457→      isolationHoursRemaining: remainingIncluded - estimatedHours
   458→    };
   459→  }
   460→
   461→  // Calculate overage
   462→  const overageMinutes = estimatedMinutes - (remainingIncluded * 60);
   463→  const overageCost = overageMinutes * ISOLATION_OVERAGE_RATE;
   464→
   465→  return {
   466→    allowed: true,
   467→    reason: 'overage',
   468→    message: `${remainingIncluded > 0 ? `Using ${(remainingIncluded * 60).toFixed(1)} included min + ` : ''}${overageMinutes.toFixed(1)} min overage ($${overageCost.toFixed(2)})`,
   469→    hasIncludedHours: remainingIncluded > 0,
   470→    overageRequired: true,
   471→    overageMinutes,
   472→    overageCost,
   473→    isolationHoursRemaining: 0
   474→  };
   475→}
