   477→/**
   478→ * Deduct isolation usage from user's balance
   479→ * Returns overage cost if beyond included hours
   480→ */
   481→async function deductIsolationUsage(stripeCustomerId, audioDurationSeconds, endpoint = 'isolate-vocals') {
   482→  const durationMinutes = audioDurationSeconds / 60;
   483→  const durationHours = audioDurationSeconds / 3600;
   484→
   485→  const client = await pool.connect();
   486→  try {
   487→    // Get user
   488→    const userResult = await client.query(
   489→      'SELECT * FROM users WHERE stripe_customer_id = $1',
   490→      [stripeCustomerId]
   491→    );
   492→
   493→    if (userResult.rows.length === 0) {
   494→      throw new Error('User not found');
   495→    }
   496→
   497→    const user = userResult.rows[0];
   498→    const remainingIncluded = parseFloat(user.isolation_hours_remaining || 0);
   499→
   500→    let overageCost = 0;
   501→    let hoursFromIncluded = 0;
   502→    let overageMinutes = 0;
   503→
   504→    if (remainingIncluded >= durationHours) {
   505→      // Fully covered by included hours
   506→      hoursFromIncluded = durationHours;
   507→    } else {
   508→      // Partial or full overage
   509→      hoursFromIncluded = remainingIncluded;
   510→      overageMinutes = durationMinutes - (remainingIncluded * 60);
   511→      overageCost = overageMinutes * ISOLATION_OVERAGE_RATE;
   512→    }
   513→
   514→    // Update isolation balance
   515→    await client.query(
   516→      `UPDATE users
   517→       SET isolation_hours_remaining = GREATEST(0, isolation_hours_remaining - $1),
   518→           updated_at = CURRENT_TIMESTAMP
   519→       WHERE stripe_customer_id = $2`,
   520→      [durationHours, stripeCustomerId]
   521→    );
   522→
   523→    // Log usage (mark as isolation)
   524→    await client.query(
   525→      `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   526→       VALUES ($1, $2, $3, $4, $5)`,
   527→      [user.id, stripeCustomerId, audioDurationSeconds, durationHours, endpoint]
   528→    );
   529→
   530→    // Return updated balance and overage info
   531→    const newBalance = await getBalance(stripeCustomerId);
   532→    return {
   533→      ...newBalance,
   534→      isolationUsed: {
   535→        durationMinutes,
   536→        hoursFromIncluded,
   537→        overageMinutes,
   538→        overageCost
   539→      }
   540→    };
   541→  } finally {
   542→    client.release();
   543→  }
   544→}
