    66→  console.log(`[SPLICE RMS] Options: threshold=${opts.threshold}dB, minLength=${opts.minSilenceLength}s, seekStep=${opts.seekStep}s`);
    67→
    68→  // Check if we should use chunked processing (for files longer than 45 seconds)
    69→  if (opts.chunkingEnabled !== false) {
    70→    const duration = await getAudioDuration(validatedPath);
    71→    if (duration > 45) {
    72→      console.log(`[SPLICE RMS] Long file detected (${duration}s), using chunked processing`);
    73→      return detectSilencesRMSChunked(validatedPath, options);
    74→    }
    75→  }
    76→
    77→  // Step 1: Extract raw audio data using FFmpeg
    78→  const audioData = await extractAudioData(validatedPath);
    79→
    80→  // Step 2: Calculate RMS values for sliding windows
    81→  const rmsValues = calculateRMSWindows(audioData.samples, {
    82→    windowSize: Math.floor(opts.seekStep * audioData.sampleRate),
    83→    hopSize: Math.floor(opts.seekStep * audioData.sampleRate / 2)
    84→  });
    85→
    86→  // Step 3: Convert to dBFS
    87→  const dBFSValues = rmsValues.map(rms => rmsToDBFS(rms));
    88→
    89→  // Step 4: Determine threshold (auto-detect or use provided)
    90→  let threshold = opts.threshold;
    91→  let thresholdInfo = { method: 'manual', value: threshold };
    92→
    93→  if (opts.autoThreshold) {
    94→    const autoResult = autoDetectThreshold(dBFSValues);
    95→    threshold = autoResult.threshold;
    96→    thresholdInfo = autoResult;
    97→    console.log(`[SPLICE RMS] Auto-detected threshold: ${threshold.toFixed(1)}dB (${autoResult.method})`);
    98→  }
    99→
   100→  // Step 5: Find silence regions
   101→  const rawSilences = findSilenceRegions(dBFSValues, {
   102→    threshold,
   103→    seekStep: opts.seekStep,
   104→    minSilenceLength: opts.minSilenceLength
   105→  });
   106→
   107→  // Step 6: Merge adjacent silences
   108→  const mergedSilences = mergeSilences(rawSilences, opts.mergeDistance);
   109→
   110→  // Step 7: Apply padding
   111→  const paddedSilences = applyPadding(mergedSilences, {
   112→    paddingStart: opts.paddingStart,
   113→    paddingEnd: opts.paddingEnd,
   114→    audioDuration: audioData.duration
   115→  });
   116→
   117→  // Calculate statistics
   118→  const totalSilenceDuration = paddedSilences.reduce((sum, s) => sum + s.duration, 0);
   119→  const speechDuration = audioData.duration - totalSilenceDuration;
   120→
   121→  console.log(`[SPLICE RMS] Detected ${paddedSilences.length} silence(s), total ${totalSilenceDuration.toFixed(2)}s`);
   122→
   123→  return {
   124→    silences: paddedSilences,
   125→    metadata: {
   126→      audioDuration: audioData.duration,
   127→      sampleRate: audioData.sampleRate,
   128→      channels: audioData.channels,
   129→      silenceCount: paddedSilences.length,
   130→      totalSilenceDuration,
   131→      speechDuration,
   132→      silencePercentage: ((totalSilenceDuration / audioData.duration) * 100).toFixed(1),
   133→      threshold: thresholdInfo,
   134→      rmsAnalysis: {
   135→        windowCount: dBFSValues.length,
   136→        minDB: Math.min(...dBFSValues).toFixed(1),
   137→        maxDB: Math.max(...dBFSValues).toFixed(1),
   138→        avgDB: (dBFSValues.reduce((a, b) => a + b, 0) / dBFSValues.length).toFixed(1)
   139→      }
   140→    }
   141→  };
