   144→// =============================================================================
   145→// Chunked Audio Processing
   146→// =============================================================================
   147→
   148→/**
   149→ * Detect silences using chunked RMS analysis for long files
   150→ * Processes audio in chunks to reduce memory usage
   151→ *
   152→ * @param {string} audioPath - Path to audio file
   153→ * @param {Object} options - Detection options including chunkDuration
   154→ * @returns {Promise<Object>} Detection results with silences and metadata
   155→ */
   156→async function detectSilencesRMSChunked(audioPath, options = {}) {
   157→  const opts = { ...DEFAULT_OPTIONS, ...options };
   158→  const chunkDuration = opts.chunkDuration || 30;
   159→
   160→  // SECURITY: Validate input path to prevent path traversal
   161→  // (may already be validated by caller, but double-check for safety)
   162→  const pathValidation = await validateAudioPath(audioPath);
   163→  if (!pathValidation.valid) {
   164→    throw new Error(`Invalid audio path: ${pathValidation.error}`);
   165→  }
   166→  const validatedPath = pathValidation.path;
   167→
   168→  console.log(`[SPLICE RMS] Chunked analysis: ${validatedPath}`);
   169→
   170→  // Get audio duration first
   171→  const duration = await getAudioDuration(validatedPath);
   172→  const numChunks = Math.ceil(duration / chunkDuration);
   173→
   174→  console.log(`[SPLICE RMS] Duration: ${duration}s, processing in ${numChunks} chunk(s) of ${chunkDuration}s`);
   175→
   176→  // If short enough, use regular processing
   177→  if (duration <= chunkDuration * 1.5) {
   178→    return detectSilencesRMS(validatedPath, { ...opts, chunkingEnabled: false });
   179→  }
   180→
   181→  let allDBFSValues = [];
   182→  let sampleRateUsed = 16000;
   183→
   184→  // Process each chunk
   185→  for (let i = 0; i < numChunks; i++) {
   186→    const startTime = i * chunkDuration;
   187→    const endTime = Math.min((i + 1) * chunkDuration, duration);
   188→
   189→    console.log(`[SPLICE RMS] Processing chunk ${i + 1}/${numChunks} (${startTime}s - ${endTime}s)`);
   190→
   191→    // Extract chunk audio data
   192→    const chunkData = await extractAudioDataChunk(validatedPath, startTime, endTime - startTime);
   193→    sampleRateUsed = chunkData.sampleRate;
   194→
   195→    // Calculate RMS values for this chunk
   196→    const rmsValues = calculateRMSWindows(chunkData.samples, {
   197→      windowSize: Math.floor(opts.seekStep * chunkData.sampleRate),
   198→      hopSize: Math.floor(opts.seekStep * chunkData.sampleRate / 2)
   199→    });
   200→
   201→    // Convert to dBFS
   202→    const dBFSValues = rmsValues.map(rms => rmsToDBFS(rms));
   203→    allDBFSValues = allDBFSValues.concat(Array.from(dBFSValues));
   204→  }
   205→
   206→  // Determine threshold
   207→  let threshold = opts.threshold;
   208→  let thresholdInfo = { method: 'manual', value: threshold };
   209→
   210→  if (opts.autoThreshold) {
   211→    const autoResult = autoDetectThreshold(allDBFSValues);
   212→    threshold = autoResult.threshold;
   213→    thresholdInfo = autoResult;
   214→    console.log(`[SPLICE RMS] Auto-detected threshold: ${threshold.toFixed(1)}dB`);
   215→  }
   216→
   217→  // Find silence regions
   218→  const rawSilences = findSilenceRegions(allDBFSValues, {
   219→    threshold,
   220→    seekStep: opts.seekStep,
   221→    minSilenceLength: opts.minSilenceLength
   222→  });
   223→
   224→  // Merge adjacent silences
   225→  const mergedSilences = mergeSilences(rawSilences, opts.mergeDistance);
   226→
   227→  // Apply padding
   228→  const paddedSilences = applyPadding(mergedSilences, {
   229→    paddingStart: opts.paddingStart,
   230→    paddingEnd: opts.paddingEnd,
   231→    audioDuration: duration
   232→  });
   233→
   234→  const totalSilenceDuration = paddedSilences.reduce((sum, s) => sum + s.duration, 0);
   235→
   236→  console.log(`[SPLICE RMS] Chunked detection found ${paddedSilences.length} silence(s)`);
   237→
   238→  return {
   239→    silences: paddedSilences,
   240→    metadata: {
   241→      audioDuration: duration,
   242→      sampleRate: sampleRateUsed,
   243→      channels: 1,
   244→      silenceCount: paddedSilences.length,
   245→      totalSilenceDuration,
   246→      speechDuration: duration - totalSilenceDuration,
   247→      silencePercentage: ((totalSilenceDuration / duration) * 100).toFixed(1),
   248→      threshold: thresholdInfo,
   249→      chunked: true,
   250→      chunkCount: numChunks,
   251→      rmsAnalysis: {
   252→        windowCount: allDBFSValues.length,
   253→        minDB: Math.min(...allDBFSValues).toFixed(1),
   254→        maxDB: Math.max(...allDBFSValues).toFixed(1),
   255→        avgDB: (allDBFSValues.reduce((a, b) => a + b, 0) / allDBFSValues.length).toFixed(1)
   256→      }
   257→    }
   258→  };
   259→}
