   439→// =============================================================================
   440→// RMS Calculation
   441→// =============================================================================
   442→
   443→/**
   444→ * Calculate RMS values for sliding windows across audio samples
   445→ *
   446→ * @param {Float32Array} samples - Audio samples (-1 to 1)
   447→ * @param {Object} options - Window and hop size
   448→ * @returns {Float32Array} RMS values for each window
   449→ */
   450→function calculateRMSWindows(samples, options = {}) {
   451→  const { windowSize = 800, hopSize = 400 } = options; // Default ~50ms at 16kHz
   452→
   453→  const numWindows = Math.floor((samples.length - windowSize) / hopSize) + 1;
   454→  const rmsValues = new Float32Array(numWindows);
   455→
   456→  for (let i = 0; i < numWindows; i++) {
   457→    const start = i * hopSize;
   458→    const end = Math.min(start + windowSize, samples.length);
   459→
   460→    // Calculate RMS for this window
   461→    let sumSquares = 0;
   462→    for (let j = start; j < end; j++) {
   463→      sumSquares += samples[j] * samples[j];
   464→    }
   465→
   466→    const rms = Math.sqrt(sumSquares / (end - start));
   467→    rmsValues[i] = rms;
   468→  }
   469→
   470→  return rmsValues;
   471→}
   472→
   473→/**
   474→ * Convert RMS value to dBFS (decibels relative to full scale)
   475→ *
   476→ * @param {number} rms - RMS value (0 to 1)
   477→ * @returns {number} dBFS value (negative, -inf for silence)
   478→ */
   479→function rmsToDBFS(rms) {
   480→  if (rms <= 0) return -100; // Effective silence floor
   481→  const dbfs = 20 * Math.log10(rms);
   482→  return Math.max(-100, dbfs); // Clamp to reasonable floor
   483→}
   484→
   485→// =============================================================================
   486→// Auto-Threshold Detection
   487→// =============================================================================
   488→
   489→/**
   490→ * Auto-detect optimal silence threshold from audio histogram
   491→ * Uses Gaussian blur + local maxima detection
   492→ *
   493→ * @param {Float32Array} dBFSValues - Array of dBFS values
   494→ * @returns {Object} Detected threshold and method info
   495→ */
   496→function autoDetectThreshold(dBFSValues) {
   497→  // Build histogram of dBFS values
   498→  const histBins = 100;
   499→  const minDB = -80;
   500→  const maxDB = 0;
   501→  const binWidth = (maxDB - minDB) / histBins;
   502→
   503→  const histogram = new Array(histBins).fill(0);
   504→
   505→  for (const db of dBFSValues) {
   506→    const binIndex = Math.min(histBins - 1, Math.max(0, Math.floor((db - minDB) / binWidth)));
   507→    histogram[binIndex]++;
   508→  }
   509→
   510→  // Apply Gaussian blur to smooth histogram
   511→  const smoothed = gaussianBlur(histogram, 5);
   512→
   513→  // Find local maxima (peaks)
   514→  const peaks = findLocalMaxima(smoothed, 3);
   515→
   516→  // Sort peaks by value (highest first)
   517→  peaks.sort((a, b) => b.value - a.value);
   518→
   519→  if (peaks.length >= 2) {
   520→    // Interpolate between the two highest peaks
   521→    // Assume: higher peak = speech, lower peak = silence
   522→    const peak1DB = minDB + peaks[0].index * binWidth;
   523→    const peak2DB = minDB + peaks[1].index * binWidth;
   524→
   525→    const silencePeakDB = Math.min(peak1DB, peak2DB);
   526→    const speechPeakDB = Math.max(peak1DB, peak2DB);
   527→
   528→    // Set threshold between peaks (closer to silence peak)
   529→    const threshold = silencePeakDB + (speechPeakDB - silencePeakDB) * 0.3;
   530→
   531→    return {
   532→      threshold: Math.max(-60, Math.min(-20, threshold)),
   533→      method: 'dual-peak',
   534→      silencePeak: silencePeakDB.toFixed(1),
   535→      speechPeak: speechPeakDB.toFixed(1)
   536→    };
   537→  } else if (peaks.length === 1) {
   538→    // Single peak - use percentile-based fallback
   539→    const sorted = [...dBFSValues].sort((a, b) => a - b);
   540→    const percentile25 = sorted[Math.floor(sorted.length * 0.25)];
   541→
   542→    return {
   543→      threshold: Math.max(-60, Math.min(-20, percentile25)),
   544→      method: 'percentile-25',
   545→      value: percentile25.toFixed(1)
   546→    };
   547→  } else {
   548→    // No peaks found - use conservative default
   549→    return {
   550→      threshold: -35,
   551→      method: 'default-fallback'
   552→    };
   553→  }
   554→}
   555→
   556→/**
   557→ * Apply Gaussian blur to an array
   558→ *
   559→ * @param {Array<number>} arr - Input array
   560→ * @param {number} sigma - Standard deviation
   561→ * @returns {Array<number>} Smoothed array
   562→ */
   563→function gaussianBlur(arr, sigma = 5) {
   564→  const kernelSize = Math.ceil(sigma * 3) * 2 + 1;
   565→  const kernel = [];
   566→  const halfSize = Math.floor(kernelSize / 2);
   567→
   568→  // Generate Gaussian kernel
   569→  let sum = 0;
   570→  for (let i = 0; i < kernelSize; i++) {
   571→    const x = i - halfSize;
   572→    const g = Math.exp(-(x * x) / (2 * sigma * sigma));
   573→    kernel.push(g);
   574→    sum += g;
   575→  }
   576→
   577→  // Normalize kernel
   578→  for (let i = 0; i < kernelSize; i++) {
   579→    kernel[i] /= sum;
   580→  }
   581→
   582→  // Apply convolution
   583→  const result = new Array(arr.length).fill(0);
   584→  for (let i = 0; i < arr.length; i++) {
   585→    let value = 0;
   586→    for (let j = 0; j < kernelSize; j++) {
   587→      const idx = i + j - halfSize;
   588→      if (idx >= 0 && idx < arr.length) {
   589→        value += arr[idx] * kernel[j];
   590→      }
   591→    }
   592→    result[i] = value;
   593→  }
   594→
   595→  return result;
   596→}
   597→
   598→/**
   599→ * Find local maxima in an array
   600→ *
   601→ * @param {Array<number>} arr - Input array
   602→ * @param {number} windowSize - Neighborhood size for local maximum detection
   603→ * @returns {Array<{index: number, value: number}>} Array of peak positions and values
   604→ */
   605→function findLocalMaxima(arr, windowSize = 3) {
   606→  const peaks = [];
   607→  const halfWindow = Math.floor(windowSize / 2);
   608→
   609→  for (let i = halfWindow; i < arr.length - halfWindow; i++) {
   610→    let isMax = true;
   611→    for (let j = i - halfWindow; j <= i + halfWindow; j++) {
   612→      if (j !== i && arr[j] >= arr[i]) {
   613→        isMax = false;
   614→        break;
   615→      }
   616→    }
   617→    if (isMax && arr[i] > 0) {
   618→      peaks.push({ index: i, value: arr[i] });
   619→    }
   620→  }
   621→
   622→  return peaks;
   623→}
   624→
   625→// =============================================================================
   626→// Silence Region Detection
   627→// =============================================================================
   628→
   629→/**
   630→ * Find silence regions based on dBFS threshold
   631→ *
   632→ * @param {Float32Array} dBFSValues - Array of dBFS values
   633→ * @param {Object} options - Detection options
   634→ * @returns {Array<{start: number, end: number, duration: number}>} Silence regions
   635→ */
   636→function findSilenceRegions(dBFSValues, options) {
   637→  const { threshold, seekStep, minSilenceLength } = options;
   638→
   639→  const silences = [];
   640→  let silenceStart = null;
   641→
   642→  for (let i = 0; i < dBFSValues.length; i++) {
   643→    const time = i * seekStep;
   644→    const isSilent = dBFSValues[i] < threshold;
   645→
   646→    if (isSilent && silenceStart === null) {
   647→      // Start of silence
   648→      silenceStart = time;
   649→    } else if (!isSilent && silenceStart !== null) {
   650→      // End of silence
   651→      const duration = time - silenceStart;
   652→      if (duration >= minSilenceLength) {
   653→        silences.push({
   654→          start: silenceStart,
   655→          end: time,
   656→          duration
   657→        });
   658→      }
   659→      silenceStart = null;
   660→    }
   661→  }
   662→
   663→  // Handle silence at end of audio
   664→  if (silenceStart !== null) {
   665→    const endTime = dBFSValues.length * seekStep;
   666→    const duration = endTime - silenceStart;
   667→    if (duration >= minSilenceLength) {
   668→      silences.push({
   669→        start: silenceStart,
   670→        end: endTime,
   671→        duration
   672→      });
   673→    }
   674→  }
   675→
   676→  return silences;
   677→}
   678→
   679→// =============================================================================
   680→// Post-Processing
   681→// =============================================================================
   682→
   683→/**
   684→ * Merge silences that are close together
   685→ *
   686→ * @param {Array} silences - Array of silence regions
   687→ * @param {number} mergeDistance - Maximum gap to merge (seconds)
   688→ * @returns {Array} Merged silence regions
   689→ */
   690→function mergeSilences(silences, mergeDistance) {
   691→  if (silences.length <= 1) return silences;
   692→
   693→  const merged = [];
   694→  let current = { ...silences[0] };
   695→
   696→  for (let i = 1; i < silences.length; i++) {
   697→    const next = silences[i];
   698→
   699→    // If gap is small enough, merge
   700→    if (next.start - current.end <= mergeDistance) {
   701→      current.end = next.end;
   702→      current.duration = current.end - current.start;
   703→    } else {
   704→      merged.push(current);
   705→      current = { ...next };
   706→    }
   707→  }
   708→
   709→  merged.push(current);
   710→  return merged;
   711→}
   712→
   713→/**
   714→ * Apply padding to silence boundaries
   715→ *
   716→ * @param {Array} silences - Array of silence regions
   717→ * @param {Object} options - Padding options
   718→ * @returns {Array} Padded silence regions
   719→ */
   720→function applyPadding(silences, options) {
   721→  const { paddingStart, paddingEnd, audioDuration } = options;
   722→
   723→  return silences
   724→    .map(silence => {
   725→      const paddedStart = Math.max(0, silence.start + paddingStart);
   726→      const paddedEnd = Math.min(audioDuration, silence.end - paddingEnd);
   727→      const duration = paddedEnd - paddedStart;
   728→
   729→      // Skip if padding made the silence invalid
   730→      if (duration <= 0) return null;
   731→
   732→      return {
   733→        start: parseFloat(paddedStart.toFixed(3)),
   734→        end: parseFloat(paddedEnd.toFixed(3)),
   735→        duration: parseFloat(duration.toFixed(3))
   736→      };
   737→    })
   738→    .filter(s => s !== null);
   739→}
   740→
   741→// =============================================================================
   742→// Sensitivity Mapping
   743→// =============================================================================
   744→
   745→/**
   746→ * Map UI sensitivity (0-100) to detection parameters
   747→ * Based on Fireside's aggressiveness mapping
   748→ *
   749→ * @param {number} sensitivity - UI sensitivity value (0-100)
   750→ * @returns {Object} Detection parameters
   751→ */
   752→function sensitivityToParams(sensitivity) {
   753→  // Clamp to 0-100
   754→  const s = Math.max(0, Math.min(100, sensitivity));
   755→
   756→  // Linear interpolation helper
   757→  const lerp = (min, max, t) => min + (max - min) * (t / 100);
   758→
   759→  return {
