   821→/**
   822→ * POST /fillers - Detect filler words (um, uh, like, etc.)
   823→ *
   824→ * Transcribes audio and identifies filler words with timestamps.
   825→ * Returns segments that can be cut or reviewed for removal.
   826→ *
   827→ * Options:
   828→ * - wavPath: Path to audio file (required unless transcript provided)
   829→ * - transcript: Pre-existing transcript with word-level timing (optional)
   830→ * - customFillers: Additional filler words to detect (optional)
   831→ */
   832→app.post('/fillers', requireCredits({ endpoint: 'fillers' }), async (req, res) => {
   833→  const {
   834→    wavPath,
   835→    transcript: providedTranscript,
   836→    customFillers = []
   837→  } = req.body;
   838→
   839→  if (!wavPath && !providedTranscript) {
   840→    return res.status(400).json({ error: 'wavPath or transcript is required' });
   841→  }
   842→
   843→  if (wavPath && !fs.existsSync(wavPath)) {
   844→    return res.status(404).json({ error: `File not found: ${wavPath}` });
   845→  }
   846→
   847→  console.log(`[SPLICE] Filler word detection: ${wavPath || 'provided transcript'}`);
   848→
   849→  try {
   850→    // Get or create transcript with word-level timestamps
   851→    let transcript = providedTranscript;
   852→    if (!transcript && wavPath) {
   853→      transcript = await transcribeWithWords(wavPath);
   854→    }
   855→
   856→    // Validate transcript has words
   857→    if (!transcript || !transcript.words || transcript.words.length === 0) {
   858→      return res.status(400).json({
   859→        error: 'Transcript must contain word-level timing data'
   860→      });
   861→    }
   862→
   863→    // Default filler words (common in English speech)
   864→    const defaultFillers = [
   865→      'um', 'uh', 'ah', 'er', 'eh',           // Hesitation sounds
   866→      'like', 'so', 'well', 'right',           // Discourse markers
   867→      'you know', 'i mean', 'basically',       // Filler phrases
   868→      'actually', 'literally', 'honestly',     // Overused qualifiers
   869→      'kind of', 'sort of', 'you see'          // Hedging phrases
   870→    ];
   871→
   872→    // Combine default + custom fillers (lowercase for matching)
   873→    const fillerSet = new Set([
   874→      ...defaultFillers,
   875→      ...customFillers.map(f => f.toLowerCase().trim())
   876→    ]);
   877→
   878→    // Detect filler words
   879→    const fillers = [];
   880→    const words = transcript.words;
   881→
   882→    for (let i = 0; i < words.length; i++) {
   883→      const word = words[i];
   884→      const normalizedWord = word.word.toLowerCase().replace(/[.,!?;:'"]/g, '').trim();
   885→
   886→      // Check single-word fillers
   887→      if (fillerSet.has(normalizedWord)) {
   888→        fillers.push({
   889→          word: word.word,
   890→          normalizedWord,
   891→          start: word.start,
   892→          end: word.end,
   893→          duration: word.end - word.start,
   894→          index: i,
   895→          type: 'filler'
   896→        });
   897→        continue;
   898→      }
   899→
   900→      // Check two-word phrases (e.g., "you know", "kind of")
   901→      if (i < words.length - 1) {
   902→        const nextWord = words[i + 1];
   903→        const twoWordPhrase = `${normalizedWord} ${nextWord.word.toLowerCase().replace(/[.,!?;:'"]/g, '').trim()}`;
   904→        if (fillerSet.has(twoWordPhrase)) {
   905→          fillers.push({
   906→            word: `${word.word} ${nextWord.word}`,
   907→            normalizedWord: twoWordPhrase,
   908→            start: word.start,
   909→            end: nextWord.end,
   910→            duration: nextWord.end - word.start,
   911→            index: i,
   912→            type: 'filler_phrase'
   913→          });
   914→          // Skip next word since it's part of this phrase
   915→          i++;
   916→        }
   917→      }
   918→    }
   919→
   920→    // Calculate total filler time
   921→    const totalFillerDuration = fillers.reduce((sum, f) => sum + f.duration, 0);
   922→    const audioDuration = transcript.duration || (words.length > 0 ? words[words.length - 1].end : 0);
   923→    const fillerPercentage = audioDuration > 0 ? (totalFillerDuration / audioDuration) * 100 : 0;
   924→
   925→    // Deduct usage based on audio duration
   926→    let balance = null;
   927→    if (audioDuration > 0 && req.deductUsage) {
   928→      balance = await req.deductUsage(audioDuration);
   929→    }
   930→
   931→    res.json({
   932→      success: true,
   933→      wavPath,
   934→      fillers,
   935→      metadata: {
   936→        totalWords: words.length,
   937→        fillerCount: fillers.length,
   938→        totalFillerDuration: parseFloat(totalFillerDuration.toFixed(3)),
   939→        audioDuration: parseFloat(audioDuration.toFixed(3)),
   940→        fillerPercentage: parseFloat(fillerPercentage.toFixed(2)),
   941→        fillersPerMinute: audioDuration > 0 ? parseFloat((fillers.length / (audioDuration / 60)).toFixed(2)) : 0
   942→      },
   943→      removalSegments: fillers.map(f => ({
   944→        start: f.start,
   945→        end: f.end,
   946→        duration: f.duration,
   947→        reason: `Filler: "${f.word}"`,
   948→        type: f.type
   949→      })),
   950→      balance: balance ? { hoursRemaining: balance.hoursRemaining, tier: balance.tier } : undefined
   951→    });
   952→  } catch (err) {
   953→    console.error('[SPLICE] Filler detection error:', err);
   954→    res.status(500).json({ error: err.message });
   955→  }
   956→});
