  1600→// =============================================================================
  1601→// Batch Processing Routes
  1602→// =============================================================================
  1603→
  1604→// In-memory job queue for batch processing
  1605→const batchJobs = new Map();
  1606→
  1607→// Batch job limits to prevent memory leak
  1608→const MAX_BATCH_JOBS = 10000;
  1609→const BATCH_JOB_MAX_AGE_MS = 24 * 60 * 60 * 1000; // 24 hours
  1610→
  1611→/**
  1612→ * Generate a unique job ID
  1613→ */
  1614→function generateJobId() {
  1615→  return `batch_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  1616→}
  1617→
  1618→/**
  1619→ * Clean up old batch jobs to prevent memory leak
  1620→ * Removes jobs older than 24 hours
  1621→ */
  1622→function cleanupOldBatchJobs() {
  1623→  const now = Date.now();
  1624→  let removedCount = 0;
  1625→
  1626→  for (const [jobId, job] of batchJobs.entries()) {
  1627→    const createdAt = new Date(job.createdAt).getTime();
  1628→    if (now - createdAt > BATCH_JOB_MAX_AGE_MS) {
  1629→      batchJobs.delete(jobId);
  1630→      removedCount++;
  1631→    }
  1632→  }
