  1639→/**
  1640→ * Enforce max job limit by removing oldest completed jobs
  1641→ */
  1642→function enforceJobLimit() {
  1643→  if (batchJobs.size < MAX_BATCH_JOBS) return;
  1644→
  1645→  // Get completed jobs sorted by creation date (oldest first)
  1646→  const completedJobs = Array.from(batchJobs.entries())
  1647→    .filter(([_, job]) => job.status !== 'processing')
  1648→    .sort((a, b) => new Date(a[1].createdAt) - new Date(b[1].createdAt));
  1649→
  1650→  // Remove oldest completed jobs until under limit
  1651→  const toRemove = batchJobs.size - MAX_BATCH_JOBS + 1;
  1652→  for (let i = 0; i < Math.min(toRemove, completedJobs.length); i++) {
  1653→    batchJobs.delete(completedJobs[i][0]);
  1654→  }
  1655→
  1656→  console.log(`[SPLICE] Enforced job limit, removed ${Math.min(toRemove, completedJobs.length)} job(s)`);
  1657→}
