  1662→/**
  1663→ * POST /batch/silences - Process multiple files for silence detection
  1664→ *
  1665→ * Creates a batch job that processes multiple audio files.
  1666→ * Returns a job ID for tracking progress.
  1667→ *
  1668→ * Body:
  1669→ * - files: Array of file paths to process
  1670→ * - options: Detection options (sensitivity, threshold, etc.)
  1671→ */
  1672→app.post('/batch/silences', requireCredits({ endpoint: 'batch-silences' }), async (req, res) => {
  1673→  const { files, options = {} } = req.body;
  1674→
  1675→  if (!files || !Array.isArray(files) || files.length === 0) {
  1676→    return res.status(400).json({ error: 'files array is required' });
  1677→  }
  1678→
  1679→  // Validate all files exist
  1680→  const missingFiles = files.filter(f => !fs.existsSync(f));
  1681→  if (missingFiles.length > 0) {
  1682→    return res.status(404).json({
  1683→      error: 'Some files not found',
  1684→      missingFiles
  1685→    });
  1686→  }
  1687→
  1688→  // Enforce job limit before creating new job
  1689→  enforceJobLimit();
  1690→
  1691→  const jobId = generateJobId();
  1692→
  1693→  // Initialize job with customer ID for usage tracking
  1694→  const job = {
  1695→    id: jobId,
  1696→    type: 'silences',
  1697→    status: 'processing',
  1698→    createdAt: new Date().toISOString(),
  1699→    stripeCustomerId: req.stripeCustomerId,  // Store for usage deduction
  1700→    files: files.map(f => ({
  1701→      path: f,
  1702→      status: 'pending',
  1703→      result: null,
  1704→      error: null
  1705→    })),
  1706→    options,
  1707→    progress: {
  1708→      total: files.length,
  1709→      completed: 0,
  1710→      failed: 0,
  1711→      percentage: 0
  1712→    },
  1713→    results: [],
  1714→    errors: [],
  1715→    totalUsageDeducted: 0  // Track total seconds deducted
  1716→  };
  1717→
  1718→  batchJobs.set(jobId, job);
  1719→  console.log(`[SPLICE] Batch job ${jobId} created with ${files.length} files`);
  1720→
  1721→  // Start processing in background
  1722→  processBatchJob(jobId);
  1723→
  1724→  res.json({
  1725→    success: true,
  1726→    jobId,
  1727→    message: `Batch job created with ${files.length} files`,
  1728→    statusUrl: `/batch/status/${jobId}`
  1729→  });
  1730→});
  1731→
  1732→/**
  1733→ * Process a batch job (runs in background)
  1734→ */
  1735→async function processBatchJob(jobId) {
  1736→  const job = batchJobs.get(jobId);
  1737→  if (!job) return;
  1738→
  1739→  const { sensitivity, ...manualOptions } = job.options;
  1740→
  1741→  // Build detection options
  1742→  let detectionOptions = {};
  1743→  if (typeof sensitivity === 'number') {
  1744→    detectionOptions = sensitivityToParams(sensitivity);
  1745→  } else {
  1746→    detectionOptions = {
  1747→      threshold: manualOptions.threshold ?? -30,
  1748→      minSilenceLength: manualOptions.minSilenceLength ?? 0.5,
  1749→      paddingStart: manualOptions.paddingStart ?? 0.1,
  1750→      paddingEnd: manualOptions.paddingEnd ?? 0.05,
  1751→      autoThreshold: manualOptions.autoThreshold ?? false
  1752→    };
  1753→  }
  1754→
  1755→  // Process files sequentially to avoid overwhelming the system
  1756→  for (let i = 0; i < job.files.length; i++) {
  1757→    const fileEntry = job.files[i];
  1758→    fileEntry.status = 'processing';
  1759→
  1760→    try {
  1761→      const result = await detectSilencesRMS(fileEntry.path, detectionOptions);
  1762→
  1763→      fileEntry.status = 'completed';
  1764→      fileEntry.result = {
  1765→        silences: result.silences,
  1766→        count: result.silences.length,
  1767→        totalSilenceDuration: result.metadata.totalSilenceDuration,
  1768→        audioDuration: result.metadata.audioDuration
  1769→      };
  1770→
  1771→      job.results.push({
  1772→        file: fileEntry.path,
  1773→        ...fileEntry.result
  1774→      });
  1775→
  1776→      // Deduct usage for this file
  1777→      const audioDuration = result.metadata?.audioDuration || 0;
  1778→      if (audioDuration > 0 && job.stripeCustomerId) {
  1779→        try {
  1780→          await usageTracking.deductUsage(job.stripeCustomerId, audioDuration, 'batch-silences');
  1781→          job.totalUsageDeducted += audioDuration;
  1782→        } catch (usageErr) {
  1783→          console.warn(`[SPLICE] Batch ${jobId}: Usage deduction failed:`, usageErr.message);
  1784→        }
  1785→      }
  1786→
  1787→      job.progress.completed++;
  1788→      console.log(`[SPLICE] Batch ${jobId}: ${i + 1}/${job.files.length} completed`);
  1789→    } catch (err) {
  1790→      fileEntry.status = 'failed';
  1791→      fileEntry.error = err.message;
  1792→
  1793→      job.errors.push({
  1794→        file: fileEntry.path,
  1795→        error: err.message
  1796→      });
  1797→
  1798→      job.progress.failed++;
  1799→      console.error(`[SPLICE] Batch ${jobId}: ${fileEntry.path} failed:`, err.message);
  1800→    }
  1801→
  1802→    // Update progress
  1803→    job.progress.percentage = Math.round(
  1804→      ((job.progress.completed + job.progress.failed) / job.progress.total) * 100
  1805→    );
  1806→  }
  1807→
  1808→  // Mark job as complete
  1809→  job.status = job.progress.failed === job.progress.total ? 'failed' :
  1810→               job.progress.failed > 0 ? 'completed_with_errors' : 'completed';
  1811→  job.completedAt = new Date().toISOString();
  1812→
  1813→  console.log(`[SPLICE] Batch job ${jobId} ${job.status}`);
  1814→}
