   150→/**
   151→ * Extract RMS levels from audio file
   152→ *
   153→ * @param {string} audioPath - Path to audio file
   154→ * @param {Object} options - Extraction options
   155→ * @returns {Promise<Object>} Track levels with timing
   156→ */
   157→async function extractTrackLevels(audioPath, options = {}) {
   158→  const { windowMs = 100, speakerName = 'Unknown' } = options;
   159→
   160→  // SECURITY: Validate input path to prevent path traversal
   161→  const pathValidation = await validateAudioPath(audioPath);
   162→  if (!pathValidation.valid) {
   163→    throw new Error(`Invalid audio path: ${pathValidation.error}`);
   164→  }
   165→  const validatedPath = pathValidation.path;
   166→
   167→  console.log(`[SPLICE Multitrack] Extracting levels: ${speakerName}`);
   168→
   169→  // SECURITY: Use execFile with array arguments to prevent command injection
   170→  const infoArgs = [
   171→    '-v', 'error',
   172→    '-select_streams', 'a:0',
   173→    '-show_entries', 'stream=sample_rate,duration',
   174→    '-of', 'json',
   175→    validatedPath
   176→  ];
   177→  let sampleRate = 48000;
   178→  let duration = 0;
   179→
   180→  try {
   181→    const { stdout } = await safeFFprobe(infoArgs);
   182→    const info = JSON.parse(stdout);
   183→    if (info.streams?.[0]) {
   184→      sampleRate = parseInt(info.streams[0].sample_rate) || 48000;
   185→      duration = parseFloat(info.streams[0].duration) || 0;
   186→    }
   187→  } catch (err) {
   188→    console.warn(`[SPLICE Multitrack] Could not get audio info: ${err.message}`);
   189→  }
   190→
   191→  // Get duration from format if not in stream
   192→  if (!duration) {
   193→    try {
   194→      const durationArgs = [
   195→        '-v', 'error',
   196→        '-show_entries', 'format=duration',
   197→        '-of', 'default=noprint_wrappers=1:nokey=1',
   198→        validatedPath
   199→      ];
   200→      const { stdout } = await safeFFprobe(durationArgs);
   201→      duration = parseFloat(stdout.trim()) || 0;
   202→    } catch (err) {
   203→      console.warn(`[SPLICE Multitrack] Could not get duration: ${err.message}`);
   204→    }
   205→  }
   206→
   207→  // SECURITY: Use safe temp path generation
   208→  const tempFile = safeTempPath('splice_multitrack', '.raw');
   209→
   210→  try {
   211→    // SECURITY: Use execFile with array arguments to prevent command injection
   212→    const extractArgs = [
   213→      '-y',
   214→      '-i', validatedPath,
   215→      '-ac', '1',
   216→      '-ar', '16000',
   217→      '-f', 's16le',
   218→      '-acodec', 'pcm_s16le',
   219→      tempFile
   220→    ];
   221→    await safeFFmpeg(extractArgs);
   222→
   223→    // PERFORMANCE: Use async file read to avoid blocking event loop
   224→    const rawData = await fsPromises.readFile(tempFile);
   225→    const samples = new Float32Array(rawData.length / 2);
   226→    for (let i = 0; i < samples.length; i++) {
   227→      samples[i] = rawData.readInt16LE(i * 2) / 32768.0;
   228→    }
   229→
   230→    // Calculate RMS per window
   231→    const windowSamples = Math.floor((windowMs / 1000) * 16000);
   232→    const hopSamples = windowSamples; // Non-overlapping windows
   233→    const numWindows = Math.floor(samples.length / hopSamples);
   234→
   235→    const levels = new Float32Array(numWindows);
   236→    const times = new Float32Array(numWindows);
   237→
   238→    for (let i = 0; i < numWindows; i++) {
   239→      const start = i * hopSamples;
   240→      const end = Math.min(start + windowSamples, samples.length);
   241→
   242→      let sumSquares = 0;
   243→      for (let j = start; j < end; j++) {
   244→        sumSquares += samples[j] * samples[j];
   245→      }
   246→
   247→      const rms = Math.sqrt(sumSquares / (end - start));
   248→      const dbfs = rms > 0 ? 20 * Math.log10(rms) : -100;
   249→
   250→      levels[i] = Math.max(-100, dbfs);
   251→      times[i] = (start / 16000);
   252→    }
   253→
   254→    // PERFORMANCE: Use async file operations
   255→    await fsPromises.unlink(tempFile);
   256→
   257→    return { levels, times, duration, speakerName, sampleRate: 16000 };
   258→  } catch (err) {
   259→    // PERFORMANCE: Use async file operations for cleanup
   260→    try {
   261→      await fsPromises.access(tempFile);
   262→      await fsPromises.unlink(tempFile);
   263→    } catch {
   264→      // File doesn't exist
   265→    }
   266→    throw new Error(`Failed to extract track levels: ${err.message}`);
   267→  }
   268→}
   269→
   270→// =============================================================================
   271→// Level Processing
   272→// =============================================================================
   273→
   274→/**
   275→ * Clean up audio levels by subtracting noise floor
   276→ *
   277→ * @param {Array} trackLevels - Array of track level objects
   278→ * @param {Object} options - Cleanup options
   279→ * @returns {Array} Cleaned level arrays
   280→ */
   281→function cleanupAudioLevels(trackLevels, options = {}) {
   282→  const numTracks = trackLevels.length;
   283→  const numSamples = trackLevels[0].levels.length;
   284→
   285→  // Calculate average level (noise floor estimate)
   286→  const averageLevels = new Float32Array(numSamples);
   287→  for (let i = 0; i < numSamples; i++) {
   288→    let sum = 0;
   289→    for (let t = 0; t < numTracks; t++) {
   290→      sum += trackLevels[t].levels[i];
   291→    }
   292→    averageLevels[i] = sum / numTracks;
   293→  }
   294→
   295→  // Subtract average from each track (relative loudness)
   296→  const cleaned = trackLevels.map(track => {
   297→    const cleanedLevels = new Float32Array(numSamples);
   298→    for (let i = 0; i < numSamples; i++) {
   299→      // Normalize to -100 to 0 range
   300→      cleanedLevels[i] = Math.max(-100, Math.min(0, track.levels[i] - averageLevels[i]));
   301→    }
   302→    return {
   303→      ...track,
   304→      levels: cleanedLevels
   305→    };
   306→  });
   307→
   308→  return cleaned;
   309→}
   310→
   311→/**
   312→ * Apply per-speaker dB boosts
   313→ *
   314→ * @param {Array} trackLevels - Cleaned track levels
   315→ * @param {Object} boosts - Speaker boost mapping { "Speaker 1": 5, ... }
   316→ * @returns {Array} Boosted levels
   317→ */
   318→function applySpeakerBoosts(trackLevels, boosts = {}) {
   319→  return trackLevels.map(track => {
   320→    const boost = boosts[track.speakerName] || 0;
   321→    if (boost === 0) return track;
   322→
   323→    const boostedLevels = new Float32Array(track.levels.length);
   324→    for (let i = 0; i < track.levels.length; i++) {
   325→      boostedLevels[i] = Math.max(-100, Math.min(0, track.levels[i] + boost));
   326→    }
   327→
   328→    return { ...track, levels: boostedLevels };
   329→  });
   330→}
   331→
   332→/**
   333→ * Apply Gaussian smoothing to reduce noise
   334→ *
   335→ * @param {Array} trackLevels - Track levels
   336→ * @param {number} windowSize - Gaussian kernel size
   337→ * @returns {Array} Smoothed levels
   338→ */
   339→function smoothAudioLevels(trackLevels, windowSize = 5) {
   340→  const kernel = generateGaussianKernel(windowSize);
   341→
   342→  return trackLevels.map(track => {
   343→    const smoothed = applyConvolution(track.levels, kernel);
   344→    return { ...track, levels: smoothed };
   345→  });
   346→}
   347→
   348→/**
   349→ * Generate Gaussian kernel
   350→ */
   351→function generateGaussianKernel(size) {
   352→  const sigma = size / 3;
   353→  const kernel = new Float32Array(size);
   354→  const half = Math.floor(size / 2);
   355→  let sum = 0;
   356→
   357→  for (let i = 0; i < size; i++) {
   358→    const x = i - half;
   359→    kernel[i] = Math.exp(-(x * x) / (2 * sigma * sigma));
   360→    sum += kernel[i];
   361→  }
   362→
   363→  // Normalize
   364→  for (let i = 0; i < size; i++) {
   365→    kernel[i] /= sum;
   366→  }
   367→
   368→  return kernel;
   369→}
   370→
   371→/**
   372→ * Apply 1D convolution
   373→ */
   374→function applyConvolution(data, kernel) {
   375→  const result = new Float32Array(data.length);
   376→  const half = Math.floor(kernel.length / 2);
   377→
   378→  for (let i = 0; i < data.length; i++) {
   379→    let sum = 0;
   380→    for (let j = 0; j < kernel.length; j++) {
   381→      const idx = i + j - half;
   382→      if (idx >= 0 && idx < data.length) {
   383→        sum += data[idx] * kernel[j];
   384→      }
   385→    }
   386→    result[i] = sum;
   387→  }
   388→
   389→  return result;
   390→}
   391→
   392→// =============================================================================
   393→// Switching Decision Generation
   394→// =============================================================================
   395→
   396→/**
   397→ * Generate video switching decisions based on audio levels
   398→ *
   399→ * @param {Array} trackLevels - Processed track levels
   400→ * @param {Object} options - Decision options
   401→ * @returns {Array} Switching decisions
   402→ */
   403→function generateSwitchingDecisions(trackLevels, options = {}) {
   404→  const {
   405→    videoTrackMapping = {},
   406→    minShotDuration = 2.0,
   407→    switchingFrequency = 50,
   408→    wideShotEnabled = true,
   409→    wideShotTolerance = 6,
   410→    wideShotTracks = [],
   411→    frameRate = 30
   412→  } = options;
   413→
   414→  const numSamples = trackLevels[0].levels.length;
   415→  const times = trackLevels[0].times;
   416→  const decisions = [];
   417→
   418→  // Calculate min samples between cuts based on switching frequency
   419→  const minSamplesPerCut = Math.ceil(minShotDuration / (times[1] - times[0]));
   420→  const switchingCooldown = Math.ceil(minSamplesPerCut * (1 - switchingFrequency / 100));
   421→
   422→  let currentSpeaker = -1;
   423→  let currentDecisionStart = 0;
   424→  let lastSwitchSample = 0;
   425→
   426→  for (let i = 0; i < numSamples; i++) {
   427→    // Find loudest speaker
   428→    let maxLevel = -100;
   429→    let loudestSpeaker = 0;
   430→    let speakersAboveThreshold = [];
   431→
   432→    for (let t = 0; t < trackLevels.length; t++) {
   433→      const level = trackLevels[t].levels[i];
   434→      if (level > maxLevel) {
   435→        maxLevel = level;
   436→        loudestSpeaker = t;
   437→      }
   438→
   439→      // Track speakers within tolerance of max (for wide shot detection)
   440→      if (wideShotEnabled && level >= maxLevel - wideShotTolerance) {
   441→        speakersAboveThreshold.push(t);
   442→      }
   443→    }
   444→
   445→    // Determine if this should be a wide shot
   446→    const isWideShot = wideShotEnabled && speakersAboveThreshold.length >= 2;
   447→    const effectiveSpeaker = isWideShot ? -1 : loudestSpeaker; // -1 = wide shot
   448→
   449→    // Check if we should switch
   450→    const canSwitch = (i - lastSwitchSample) >= switchingCooldown;
   451→    const shouldSwitch = canSwitch && effectiveSpeaker !== currentSpeaker;
   452→
   453→    if (shouldSwitch || i === 0) {
   454→      // Close previous decision
   455→      if (i > 0 && decisions.length > 0) {
   456→        decisions[decisions.length - 1].endTime = times[i];
   457→        decisions[decisions.length - 1].endSample = i;
   458→      }
   459→
   460→      // Start new decision
   461→      const speakerName = isWideShot
   462→        ? 'Wide Shot'
   463→        : trackLevels[loudestSpeaker].speakerName;
   464→
   465→      const videoTrack = isWideShot
   466→        ? (wideShotTracks[0] ?? 0)
   467→        : (videoTrackMapping[loudestSpeaker] ?? loudestSpeaker);
   468→
   469→      decisions.push({
   470→        speakerIndex: effectiveSpeaker,
   471→        speakerName,
   472→        speakers: speakersAboveThreshold.map(s => trackLevels[s].speakerName),
   473→        videoTrack,
   474→        startTime: times[i],
   475→        startSample: i,
   476→        endTime: times[numSamples - 1],
   477→        endSample: numSamples - 1,
   478→        isWideShot,
   479→        reason: isWideShot ? 'Multiple speakers' : 'Loudest speaker'
   480→      });
   481→
   482→      currentSpeaker = effectiveSpeaker;
   483→      lastSwitchSample = i;
   484→    }
   485→  }
   486→
   487→  // Finalize last decision
   488→  if (decisions.length > 0) {
   489→    decisions[decisions.length - 1].endTime = times[numSamples - 1];
   490→    decisions[decisions.length - 1].endSample = numSamples - 1;
   491→  }
   492→
   493→  // Add duration to each decision
   494→  return decisions.map(d => ({
   495→    ...d,
   496→    duration: parseFloat((d.endTime - d.startTime).toFixed(3))
   497→  }));
   498→}
   499→
   500→// =============================================================================
   501→// Wide Shot Tuning
   502→// =============================================================================
   503→
   504→/**
   505→ * Fine-tune wide shot percentage using tolerance adjustment
   506→ *
   507→ * @param {Array} decisions - Initial decisions
   508→ * @param {number} targetPercentage - Target wide shot percentage
   509→ * @returns {Array} Tuned decisions
   510→ */
   511→function tuneWideShotPercentage(decisions, targetPercentage) {
   512→  const totalDuration = decisions.reduce((sum, d) => sum + d.duration, 0);
   513→  const currentWideTime = decisions
   514→    .filter(d => d.isWideShot)
   515→    .reduce((sum, d) => sum + d.duration, 0);
   516→  const currentPercentage = (currentWideTime / totalDuration) * 100;
   517→
   518→  // If within 5% of target, don't adjust
   519→  if (Math.abs(currentPercentage - targetPercentage) <= 5) {
   520→    return decisions;
   521→  }
   522→
   523→  console.log(`[SPLICE Multitrack] Wide shot: ${currentPercentage.toFixed(1)}% (target: ${targetPercentage}%)`);
   524→
   525→  // Simple approach: mark some single-speaker decisions as wide shots
   526→  // or vice versa based on whether we need more or fewer wide shots
   527→  // (Full implementation would re-run with adjusted tolerance)
   528→
   529→  return decisions; // Return as-is for now
   530→}
   531→
   532→// =============================================================================
   533→// Cutaway Insertion
   534→// =============================================================================
   535→
   536→/**
   537→ * Insert cutaways into long clips
   538→ *
   539→ * @param {Array} decisions - Switching decisions
   540→ * @param {Object} options - Cutaway options
   541→ * @returns {Array} Decisions with cutaways inserted
   542→ */
   543→function insertCutaways(decisions, options = {}) {
   544→  const {
   545→    cutawayTracks = [],
   546→    minDuration = 1.5,
   547→    maxDuration = 4.0,
   548→    maxClipDuration = 30 // Insert cutaway if clip longer than this
   549→  } = options;
   550→
   551→  if (cutawayTracks.length === 0) return decisions;
   552→
   553→  const result = [];
   554→
   555→  for (const decision of decisions) {
   556→    if (decision.duration <= maxClipDuration) {
   557→      result.push(decision);
   558→      continue;
   559→    }
   560→
   561→    // Split long clip with cutaways
   562→    const numCutaways = Math.floor(decision.duration / maxClipDuration);
   563→    const interval = decision.duration / (numCutaways + 1);
   564→
   565→    let currentStart = decision.startTime;
   566→
   567→    for (let i = 0; i < numCutaways; i++) {
   568→      // Add main clip segment
   569→      const cutawayStart = currentStart + interval - minDuration / 2;
   570→      const cutawayDuration = minDuration + Math.random() * (maxDuration - minDuration);
   571→      const cutawayEnd = cutawayStart + cutawayDuration;
   572→
   573→      result.push({
   574→        ...decision,
   575→        startTime: currentStart,
   576→        endTime: cutawayStart,
   577→        duration: cutawayStart - currentStart
   578→      });
   579→
   580→      // Add cutaway
   581→      const cutawayTrack = cutawayTracks[i % cutawayTracks.length];
   582→      result.push({
   583→        speakerIndex: -2, // Special index for cutaway
   584→        speakerName: 'Cutaway',
   585→        speakers: [],
   586→        videoTrack: cutawayTrack,
   587→        startTime: cutawayStart,
   588→        endTime: cutawayEnd,
   589→        duration: cutawayDuration,
   590→        isWideShot: false,
   591→        isCutaway: true,
   592→        reason: 'Cutaway insert'
   593→      });
   594→
   595→      currentStart = cutawayEnd;
   596→    }
   597→
   598→    // Add final segment
   599→    result.push({
   600→      ...decision,
   601→      startTime: currentStart,
   602→      endTime: decision.endTime,
   603→      duration: decision.endTime - currentStart
   604→    });
   605→  }
   606→
   607→  return result;
   608→}
   609→
   610→// =============================================================================
   611→// Statistics
   612→// =============================================================================
   613→
   614→/**
   615→ * Calculate multitrack analysis statistics
   616→ *
   617→ * @param {Array} decisions - Final decisions
   618→ * @param {number} totalDuration - Total audio duration
   619→ * @returns {Object} Statistics
