   795→                console.error('[SPLICE] License lookup error:', err);
   796→                showLoginError('Failed to look up license. Please try again.');
   797→            } finally {
   798→                lookupLicenseBtn.disabled = false;
   799→                lookupLicenseBtn.textContent = 'Lookup';
