   503→function deleteClipsAtSilencePointsInTrack(silences, trackType, trackIndex) {
   504→    silences = JSON.parse(silences);
   505→    var clips;
   506→    if (trackType == "video") {
   507→        clips = app.project.activeSequence.videoTracks[trackIndex].clips;
   508→    } else if (trackType == "audio") {
   509→        clips = app.project.activeSequence.audioTracks[trackIndex].clips;
   510→    }
   511→
   512→    var deleted = 0;
   513→    for (var i = clips.length - 1; i >= 0; i--) {
   514→        var clip = clips[i];
   515→        var start = clip.start.seconds;
   516→        var end = clip.end.seconds;
   517→        var center = (start + end) / 2;
   518→
   519→        for (var j = 0; j < silences.length; j++) {
   520→            var silence = silences[j];
   521→            var silenceCenter = (silence[0] + silence[1]) / 2;
   522→            if (silenceCenter > start && silenceCenter < end) {
   523→                clip.remove(false, false);
   524→                deleted++;
   525→                break;
   526→            }
   527→        }
   528→    }
   529→    return JSON.stringify({ deleted: deleted });
   530→}
