   532→function makeRemainingClipsContiguousInTrack(silences, deltas, trackType, trackIndex) {
   533→    silences = JSON.parse(silences);
   534→    deltas = JSON.parse(deltas);
   535→
   536→    var clips;
   537→    if (trackType == "video") {
   538→        clips = app.project.activeSequence.videoTracks[trackIndex].clips;
   539→    } else if (trackType == "audio") {
   540→        clips = app.project.activeSequence.audioTracks[trackIndex].clips;
   541→    }
   542→
   543→    for (var i = 0; i < clips.length; i++) {
   544→        var clip = clips[i];
   545→        var end = clip.end.seconds;
   546→        var num_silences = -1;
   547→
   548→        for (var j = 0; j < silences.length; j++) {
   549→            if (end > silences[j][1]) {
   550→                num_silences++;
   551→            }
   552→        }
   553→
   554→        if (num_silences > -1 && num_silences < deltas.length) {
   555→            var delta = deltas[num_silences];
   556→            var t_start = clip.start;
   557→            t_start.seconds = t_start.seconds - delta;
   558→            clip.start = t_start;
   559→            var t_end = clip.end;
   560→            t_end.seconds = t_end.seconds - delta;
   561→            clip.end = t_end;
   562→        }
   563→    }
   564→    return JSON.stringify({ success: true });
   565→}
