  1387→function linkIdenticalClipsInTracks(videoTrackIndex, audioTrackIndex) {
  1388→    var selected_clips = app.project.activeSequence.getSelection();
  1389→    for (var i = 0; i < selected_clips.length; i++) {
  1390→        selected_clips[i].setSelected(false, false);
  1391→    }
  1392→
  1393→    var video_clips = app.project.activeSequence.videoTracks[videoTrackIndex].clips;
  1394→    var audio_clips = app.project.activeSequence.audioTracks[audioTrackIndex].clips;
  1395→
  1396→    for (var i = 0; i < video_clips.numItems; i++) {
  1397→        var video_clip = video_clips[i];
  1398→        var video_start = video_clip.start.seconds;
  1399→
  1400→        for (var j = 0; j < audio_clips.numItems; j++) {
  1401→            var audio_clip = audio_clips[j];
  1402→            var audio_start = audio_clip.start.seconds;
  1403→
  1404→            if (Math.abs(video_start - audio_start) < time_tolerance) {
  1405→                video_clip.setSelected(true, false);
  1406→                audio_clip.setSelected(true, false);
  1407→                app.project.activeSequence.linkSelection();
  1408→                video_clip.setSelected(false, false);
  1409→                audio_clip.setSelected(false, false);
  1410→                break;
  1411→            }
  1412→        }
  1413→    }
  1414→    return true;
  1415→}
