  1098→function formatTime(seconds) {
  1099→  if (typeof seconds !== 'number' || isNaN(seconds)) return '0:00';
  1100→  const mins = Math.floor(seconds / 60);
  1101→  const secs = Math.floor(seconds % 60);
  1102→  return `${mins}:${secs.toString().padStart(2, '0')}`;
  1103→}
