  1511→// ============================================================================
  1512→// BATCH PROCESSING
  1513→// ============================================================================
  1514→let batchQueue = [];
  1515→let currentBatchJobId = null;
  1516→
  1517→/**
  1518→ * Initialize batch processing handlers
  1519→ */
  1520→function initBatchHandlers() {
  1521→  const addToBatchBtn = document.getElementById('addToBatchBtn');
  1522→  const startBatchBtn = document.getElementById('startBatchBtn');
  1523→  const clearBatchBtn = document.getElementById('clearBatchBtn');
  1524→  const batchSection = document.getElementById('batchSection');
  1525→
  1526→  // Show batch section in settings or after first detection
  1527→  if (batchSection) {
  1528→    batchSection.classList.remove('hidden');
  1529→  }
  1530→
  1531→  // Add files to batch queue
  1532→  if (addToBatchBtn) {
  1533→    addToBatchBtn.addEventListener('click', async () => {
  1534→      try {
  1535→        const uxpFs = require('uxp').storage.localFileSystem;
  1536→        const files = await uxpFs.getFileForOpening({
  1537→          allowMultiple: true,
  1538→          types: ['wav', 'mp3', 'mp4', 'mov', 'aac', 'm4a']
  1539→        });
  1540→
  1541→        if (files && files.length > 0) {
  1542→          for (const file of files) {
  1543→            const path = file.nativePath || file.name;
  1544→            if (!batchQueue.includes(path)) {
  1545→              batchQueue.push(path);
  1546→            }
  1547→          }
  1548→          updateBatchQueueDisplay();
  1549→          setStatus(`Added ${files.length} file(s) to batch queue`);
  1550→        }
  1551→      } catch (err) {
  1552→        setStatus('Error adding files: ' + err.message);
  1553→      }
  1554→    });
  1555→  }
  1556→
  1557→  // Start batch processing
  1558→  if (startBatchBtn) {
  1559→    startBatchBtn.addEventListener('click', async () => {
  1560→      if (batchQueue.length === 0) {
  1561→        setStatus('No files in batch queue');
  1562→        return;
  1563→      }
  1564→
  1565→      startBatchBtn.disabled = true;
  1566→      addToBatchBtn.disabled = true;
  1567→
  1568→      try {
  1569→        await runBatchProcessing();
  1570→      } finally {
  1571→        startBatchBtn.disabled = false;
  1572→        addToBatchBtn.disabled = false;
  1573→      }
  1574→    });
  1575→  }
  1576→
  1577→  // Clear batch queue
  1578→  if (clearBatchBtn) {
  1579→    clearBatchBtn.addEventListener('click', () => {
  1580→      batchQueue = [];
  1581→      currentBatchJobId = null;
  1582→      updateBatchQueueDisplay();
  1583→      setStatus('Batch queue cleared');
  1584→    });
  1585→  }
  1586→}
  1587→
  1588→/**
  1589→ * Update batch queue display
  1590→ */
  1591→function updateBatchQueueDisplay() {
  1592→  const batchEmpty = document.getElementById('batchEmpty');
  1593→  const batchList = document.getElementById('batchList');
  1594→  const startBatchBtn = document.getElementById('startBatchBtn');
  1595→
  1596→  if (batchQueue.length === 0) {
  1597→    if (batchEmpty) batchEmpty.style.display = 'block';
  1598→    if (batchList) batchList.innerHTML = '';
  1599→    if (startBatchBtn) startBatchBtn.disabled = true;
  1600→    return;
  1601→  }
  1602→
  1603→  if (batchEmpty) batchEmpty.style.display = 'none';
  1604→  if (startBatchBtn) startBatchBtn.disabled = false;
  1605→
  1606→  if (batchList) {
  1607→    batchList.innerHTML = batchQueue.map((path, i) => {
  1608→      const fileName = path.split('/').pop();
  1609→      return `<div style="display: flex; justify-content: space-between; align-items: center; padding: 4px 6px; background: #2a2a2a; border-radius: 3px; margin-bottom: 2px; font-size: 9px;">
  1610→        <span style="overflow: hidden; text-overflow: ellipsis; white-space: nowrap; max-width: 180px;" title="${path}">${fileName}</span>
  1611→        <button onclick="removeBatchItem(${i})" style="background: transparent; border: none; color: #888; cursor: pointer; font-size: 10px;">x</button>
  1612→      </div>`;
  1613→    }).join('');
  1614→  }
  1615→}
