  1675→/**
  1676→ * Poll batch job status
  1677→ */
  1678→async function pollBatchStatus(jobId, progressBar, progressText) {
  1679→  const pollInterval = 2000; // 2 seconds
  1680→  const maxPolls = 300; // 10 minutes max
  1681→  let polls = 0;
  1682→
  1683→  while (polls < maxPolls) {
  1684→    try {
  1685→      const response = await fetch(`${BACKEND_URL}/batch/status/${jobId}`, {
  1686→        headers: getAuthHeaders()
  1687→      });
  1688→
  1689→      if (!response.ok) {
  1690→        throw new Error('Failed to get batch status');
  1691→      }
  1692→
  1693→      const data = await response.json();
  1694→      const job = data.job;
  1695→
  1696→      // Update progress
  1697→      if (progressBar) progressBar.value = job.progress.percentage;
  1698→      if (progressText) progressText.textContent = `${job.progress.completed + job.progress.failed} / ${job.progress.total} files`;
  1699→
  1700→      // Check if complete
  1701→      if (job.status !== 'processing') {
  1702→        if (job.status === 'completed') {
  1703→          setStatus(`Batch complete: ${job.progress.completed} files processed`);
  1704→        } else if (job.status === 'completed_with_errors') {
  1705→          setStatus(`Batch complete with ${job.progress.failed} error(s)`);
  1706→        } else {
  1707→          setStatus('Batch failed');
  1708→        }
  1709→
  1710→        // Clear queue on completion
  1711→        batchQueue = [];
  1712→        updateBatchQueueDisplay();
  1713→        return;
  1714→      }
  1715→
  1716→      await new Promise(resolve => setTimeout(resolve, pollInterval));
  1717→      polls++;
  1718→    } catch (err) {
  1719→      setStatus('Polling error: ' + err.message);
  1720→      return;
  1721→    }
  1722→  }
  1723→
  1724→  setStatus('Batch timed out');
  1725→}
  1726→
  1727→// Expose for inline onclick
  1728→if (typeof window !== 'undefined') {
  1729→  window.removeBatchItem = removeBatchItem;
  1730→}
