   332→    }
   333→
   334→    const exportData = {
   335→      version: 1,
   336→      exportedAt: new Date().toISOString(),
   337→      presets: customPresets
   338→    };
   339→
   340→    return {
   341→      success: true,
   342→      data: JSON.stringify(exportData, null, 2),
   343→      count: customPresets.length
