   722→
   723→  // Apply preset settings
   724→  const newSettings = {
   725→    ...preset.settings,
   726→    activePreset: presetName
   727→  };
   728→
   729→  saveSettings(newSettings);
   730→  console.log(`[SPLICE] Applied preset: ${preset.name}`);
   731→
   732→  return getSettings();
