     1→/**
     2→ * Slice 9: XML-Based Razor (Custom Razor API)
     3→ *
     4→ * Implements actual clip splitting via FCP XML export/modify/import workflow.
     5→ * This is the proven approach used by commercial tools like TimeBolt and Recut.
     6→ *
     7→ * Workflow:
     8→ * 1. Export sequence to FCP XML (via Premiere's built-in export)
     9→ * 2. Detect silences using FFprobe (actual audio analysis)
    10→ * 3. Send XML + silences to backend for processing
    11→ * 4. Import processed XML as new sequence
    12→ */
    13→
    14→// XML export path for razor workflow
    15→let xmlExportPath = '/tmp/splice_export.xml';
    16→
    17→/**
    18→ * Initialize Slice 9 - Razor functionality
    19→ * Uses silences detected from the unified detection flow (Slice 8)
    20→ */
    21→function initSlice9() {
    22→  initRazorButton();
    23→  initExportXMLButton();
    24→  checkFFprobeAvailability();
    25→}
    26→
    27→/**
    28→ * Check if FFprobe is available on the system
    29→ */
    30→async function checkFFprobeAvailability() {
    31→  try {
    32→    const response = await fetch(`${BACKEND_URL}/ffprobe-check`);
    33→    const data = await response.json();
    34→
    35→    if (!data.installed) {
    36→      console.warn('[SPLICE] FFprobe not installed:', data.message);
    37→      // Show warning in UI
    38→      const advancedSection = document.getElementById('advancedSection');
    39→      if (advancedSection) {
    40→        const warning = document.createElement('p');
    41→        warning.style.cssText = 'color: #ff6b6b; font-size: 10px; margin: 5px 0;';
    42→        warning.textContent = 'FFprobe not found. Install: brew install ffmpeg';
    43→        advancedSection.prepend(warning);
    44→      }
    45→    } else {
    46→      console.log('[SPLICE] FFprobe available');
    47→    }
    48→  } catch (err) {
    49→    console.warn('[SPLICE] Could not check FFprobe status:', err.message);
    50→  }
    51→}
    52→
    53→/**
    54→ * Initialize Export XML button
    55→ */
    56→function initExportXMLButton() {
    57→  const btn = document.getElementById('exportXMLBtn');
    58→  if (!btn) return;
    59→
    60→  btn.addEventListener('click', async () => {
    61→    btn.disabled = true;
    62→    setStatus('Exporting sequence to XML...');
    63→
    64→    try {
    65→      await exportSequenceToXML();
    66→      setStatus('XML exported successfully');
    67→      document.getElementById('razorSilencesBtn').disabled = false;
    68→    } catch (err) {
    69→      setStatus(`Export failed: ${err.message}`);
    70→      console.error('[SPLICE] XML export error:', err);
    71→    }
    72→
    73→    btn.disabled = false;
    74→  });
    75→}
    76→
    77→/**
    78→ * Export active sequence to FCP XML
    79→ *
    80→ * Note: UXP doesn't have direct XML export API yet.
    81→ * This uses a workaround via sequence markers or requires manual export.
    82→ */
    83→async function exportSequenceToXML() {
    84→  const context = await getActiveSequence();
    85→  if (!context) {
    86→    throw new Error('No project or sequence open');
    87→  }
    88→
    89→  const { project, sequence } = context;
    90→
    91→  // Get sequence info for manual export instructions
    92→  const sequenceName = await sequence.getName();
    93→
    94→  // UXP doesn't have exportFinalCutProXML yet
    95→  // Show instructions for manual export
    96→  const exportInstructions = `
    97→To export the sequence as XML:
    98→1. File > Export > Final Cut Pro XML
    99→2. Save to: ${xmlExportPath}
   100→3. Click "Process XML" when done
   101→  `;
   102→
   103→  // Create/show export modal or instructions
   104→  const existingModal = document.getElementById('exportModal');
   105→  if (existingModal) {
   106→    existingModal.remove();
   107→  }
   108→
   109→  const modal = document.createElement('div');
   110→  modal.id = 'exportModal';
   111→  modal.style.cssText = `
   112→    position: fixed;
   113→    top: 50%;
   114→    left: 50%;
   115→    transform: translate(-50%, -50%);
   116→    background: #424242;
   117→    padding: 20px;
   118→    border-radius: 8px;
   119→    box-shadow: 0 4px 20px rgba(0,0,0,0.5);
   120→    z-index: 1000;
   121→    max-width: 350px;
   122→  `;
   123→  modal.innerHTML = `
   124→    <h3 style="margin: 0 0 15px 0; color: #e1e1e1;">Export Sequence: ${sequenceName}</h3>
   125→    <p style="font-size: 12px; color: #b0b0b0; white-space: pre-line;">${exportInstructions}</p>
   126→    <div style="margin-top: 15px; text-align: right;">
   127→      <button id="closeExportModal" style="background: #666; padding: 8px 16px; border: none; border-radius: 4px; color: white; cursor: pointer;">
   128→        Got it
   129→      </button>
   130→    </div>
