     1→/**
     2→ * SPLICE CEP Panel - Animated Captions Module
     3→ * Word-by-word animated captions with template gallery and preview
     4→ * v4.0.0 - CEP Migration
     5→ */
     6→
     7→// ============================================================================
     8→// STATE
     9→// ============================================================================
    10→const animatedCaptionsState = {
    11→    templates: [],
    12→    selectedTemplate: 'mrbeast',
    13→    captions: null,
    14→    keywords: [],
    15→    settings: {
    16→        maxWordsPerLine: 4,
    17→        highlightKeywords: true,
    18→        insertEmojis: false,
    19→        emojiFrequency: 'medium'
    20→    },
    21→    isGenerating: false,
    22→    lastTranscript: null
    23→};
    24→
    25→// ============================================================================
    26→// DOM ELEMENT CACHE
    27→// ============================================================================
    28→const captionsUI = {};
    29→
    30→function cacheCaptionElements() {
    31→    captionsUI.section = document.getElementById('captionsSection');
    32→    captionsUI.toggle = document.getElementById('captionsToggle');
    33→    captionsUI.panel = document.getElementById('captionsPanel');
    34→    captionsUI.templateGallery = document.getElementById('captionTemplateGallery');
    35→    captionsUI.previewContainer = document.getElementById('captionPreviewContainer');
    36→    captionsUI.stats = document.getElementById('captionStats');
    37→    captionsUI.status = document.getElementById('captionStatus');
    38→
    39→    // Settings
    40→    captionsUI.wordsPerLine = document.getElementById('captionWordsPerLine');
    41→    captionsUI.highlightKeywords = document.getElementById('captionHighlightKeywords');
    42→    captionsUI.insertEmojis = document.getElementById('captionInsertEmojis');
    43→    captionsUI.emojiFrequency = document.getElementById('captionEmojiFrequency');
    44→
    45→    // Buttons
    46→    captionsUI.generateBtn = document.getElementById('generateCaptionsBtn');
    47→    captionsUI.exportSrtBtn = document.getElementById('exportCaptionsSrtBtn');
    48→    captionsUI.exportMogrtBtn = document.getElementById('exportCaptionsMogrtBtn');
    49→    captionsUI.applyBtn = document.getElementById('applyCaptionsBtn');
    50→    captionsUI.copyBtn = document.getElementById('copyCaptionsBtn');
    51→}
    52→
    53→// ============================================================================
    54→// INITIALIZATION
    55→// ============================================================================
    56→async function initAnimatedCaptions() {
    57→    console.log('[SPLICE Captions] Initializing animated captions module');
    58→
    59→    cacheCaptionElements();
    60→
    61→    if (!captionsUI.section) {
    62→        console.warn('[SPLICE Captions] Captions section not found in DOM');
    63→        return;
    64→    }
    65→
    66→    // Toggle section expand/collapse
    67→    if (captionsUI.toggle) {
    68→        captionsUI.toggle.addEventListener('click', toggleCaptionsSection);
    69→    }
    70→
    71→    // Load templates
    72→    await loadCaptionTemplates();
    73→
    74→    // Setup event listeners
    75→    setupCaptionEventListeners();
    76→
    77→    // Render template gallery
    78→    renderTemplateGallery();
    79→
    80→    console.log('[SPLICE Captions] Module initialized');
    81→}
    82→
    83→// ============================================================================
    84→// SECTION TOGGLE
    85→// ============================================================================
    86→function toggleCaptionsSection() {
    87→    if (!captionsUI.panel || !captionsUI.toggle) return;
    88→
    89→    const isNowCollapsed = captionsUI.panel.classList.toggle('collapsed');
    90→    const icon = captionsUI.toggle.querySelector('.toggle-icon');
    91→
    92→    if (icon) {
    93→        icon.textContent = isNowCollapsed ? '+' : '-';
    94→    }
    95→}
    96→
    97→// ============================================================================
    98→// LOAD TEMPLATES
    99→// ============================================================================
   100→async function loadCaptionTemplates() {
   101→    try {
   102→        const apiUrl = getBackendUrl();
   103→        const response = await fetchWithTimeout(`${apiUrl}/captions/templates`, {
   104→            method: 'GET',
   105→            headers: { 'Content-Type': 'application/json' }
   106→        }, 30000);
   107→
   108→        if (response.ok) {
   109→            const data = await response.json();
   110→            animatedCaptionsState.templates = data.templates || [];
   111→            console.log(`[SPLICE Captions] Loaded ${animatedCaptionsState.templates.length} templates`);
   112→        }
   113→    } catch (err) {
   114→        console.error('[SPLICE Captions] Failed to load templates:', err);
   115→        // Use fallback templates
   116→        animatedCaptionsState.templates = [
   117→            { id: 'mrbeast', name: 'MrBeast', description: 'Bold, uppercase, colorful' },
   118→            { id: 'hormozi', name: 'Hormozi', description: 'Clean, yellow highlight' },
   119→            { id: 'gaming', name: 'Gaming', description: 'Neon glow, glitch effects' },
   120→            { id: 'corporate', name: 'Corporate', description: 'Subtle, professional' },
   121→            { id: 'karaoke', name: 'Karaoke', description: 'Word-by-word highlight' }
   122→        ];
   123→    }
   124→}
   125→
   126→// ============================================================================
   127→// EVENT LISTENERS
   128→// ============================================================================
   129→function setupCaptionEventListeners() {
   130→    // Template gallery delegation
   131→    if (captionsUI.templateGallery) {
   132→        captionsUI.templateGallery.addEventListener('click', (e) => {
   133→            const card = e.target.closest('.caption-template-card');
   134→            if (card) {
   135→                selectCaptionTemplate(card.dataset.templateId);
   136→            }
   137→        });
   138→    }
   139→
   140→    // Generate button
   141→    if (captionsUI.generateBtn) {
   142→        captionsUI.generateBtn.addEventListener('click', generateCaptions);
   143→    }
   144→
   145→    // Export buttons
   146→    if (captionsUI.exportSrtBtn) {
   147→        captionsUI.exportSrtBtn.addEventListener('click', () => exportCaptions('srt'));
   148→    }
   149→    if (captionsUI.exportMogrtBtn) {
   150→        captionsUI.exportMogrtBtn.addEventListener('click', () => exportCaptions('mogrt'));
   151→    }
   152→
   153→    // Apply to timeline button
   154→    if (captionsUI.applyBtn) {
   155→        captionsUI.applyBtn.addEventListener('click', applyCaptionsToTimeline);
   156→    }
   157→
   158→    // Copy button
   159→    if (captionsUI.copyBtn) {
   160→        captionsUI.copyBtn.addEventListener('click', copyCaptionsToClipboard);
   161→    }
   162→
   163→    // Settings changes
   164→    if (captionsUI.wordsPerLine) {
   165→        captionsUI.wordsPerLine.addEventListener('change', (e) => {
   166→            animatedCaptionsState.settings.maxWordsPerLine = parseInt(e.target.value) || 4;
   167→        });
   168→    }
   169→    if (captionsUI.highlightKeywords) {
   170→        captionsUI.highlightKeywords.addEventListener('change', (e) => {
   171→            animatedCaptionsState.settings.highlightKeywords = e.target.checked;
   172→        });
   173→    }
   174→    if (captionsUI.insertEmojis) {
   175→        captionsUI.insertEmojis.addEventListener('change', (e) => {
   176→            animatedCaptionsState.settings.insertEmojis = e.target.checked;
   177→        });
   178→    }
   179→    if (captionsUI.emojiFrequency) {
   180→        captionsUI.emojiFrequency.addEventListener('change', (e) => {
   181→            animatedCaptionsState.settings.emojiFrequency = e.target.value;
   182→        });
   183→    }
   184→}
   185→
   186→// ============================================================================
   187→// TEMPLATE GALLERY
   188→// ============================================================================
   189→function renderTemplateGallery() {
   190→    if (!captionsUI.templateGallery) return;
   191→
   192→    const fragment = document.createDocumentFragment();
   193→
   194→    animatedCaptionsState.templates.forEach(template => {
   195→        const card = document.createElement('div');
   196→        card.className = `caption-template-card ${animatedCaptionsState.selectedTemplate === template.id ? 'selected' : ''}`;
   197→        card.dataset.templateId = template.id;
   198→        card.setAttribute('role', 'button');
   199→        card.setAttribute('tabindex', '0');
   200→        card.setAttribute('aria-label', `Select ${template.name} template`);
   201→
   202→        card.innerHTML = `
   203→            <div class="template-preview template-preview-${template.id}">
   204→                <span class="template-preview-text">Hello World</span>
   205→            </div>
   206→            <div class="template-info">
   207→                <span class="template-name">${template.name}</span>
   208→                <span class="template-desc">${template.description}</span>
   209→            </div>
   210→        `;
   211→
   212→        fragment.appendChild(card);
   213→    });
   214→
   215→    captionsUI.templateGallery.innerHTML = '';
   216→    captionsUI.templateGallery.appendChild(fragment);
   217→}
   218→
   219→function selectCaptionTemplate(templateId) {
   220→    animatedCaptionsState.selectedTemplate = templateId;
   221→
   222→    // Update UI
   223→    document.querySelectorAll('.caption-template-card').forEach(card => {
   224→        card.classList.toggle('selected', card.dataset.templateId === templateId);
   225→    });
   226→
   227→    console.log(`[SPLICE Captions] Selected template: ${templateId}`);
   228→
   229→    // Re-apply template if we have captions
   230→    if (animatedCaptionsState.captions) {
   231→        applyTemplateToExisting(templateId);
   232→    }
   233→}
   234→
   235→async function applyTemplateToExisting(templateId) {
   236→    if (!animatedCaptionsState.captions) return;
   237→
   238→    try {
   239→        const apiUrl = getBackendUrl();
   240→        const response = await fetchWithTimeout(`${apiUrl}/captions/apply-template`, {
   241→            method: 'POST',
   242→            headers: getAuthHeaders(),
   243→            body: JSON.stringify({
   244→                captionData: { captions: animatedCaptionsState.captions },
   245→                templateId
   246→            })
   247→        }, 30000);
   248→
   249→        if (response.ok) {
   250→            const data = await response.json();
   251→            animatedCaptionsState.captions = data.captions;
   252→            renderCaptionPreview();
   253→        }
   254→    } catch (err) {
   255→        console.error('[SPLICE Captions] Failed to apply template:', err);
   256→    }
   257→}
   258→
   259→// ============================================================================
   260→// CAPTION GENERATION
   261→// ============================================================================
   262→async function generateCaptions() {
   263→    const transcript = getLastTranscript();
   264→    if (!transcript) {
   265→        showCaptionStatus('No transcript available. Run transcription first.', 'error');
   266→        return;
   267→    }
   268→
   269→    // Check online status
   270→    if (typeof isOnline === 'function' && !isOnline()) {
   271→        showCaptionStatus('Offline - Check your connection', 'error');
   272→        return;
   273→    }
   274→
   275→    animatedCaptionsState.isGenerating = true;
   276→    updateGenerateButton(true);
   277→    showCaptionStatus('Generating animated captions...', 'info');
   278→
   279→    try {
   280→        const apiUrl = getBackendUrl();
   281→        const response = await fetchWithTimeout(`${apiUrl}/captions/animate`, {
   282→            method: 'POST',
   283→            headers: getAuthHeaders(),
   284→            body: JSON.stringify({
   285→                transcript,
   286→                template: animatedCaptionsState.selectedTemplate,
   287→                settings: animatedCaptionsState.settings
   288→            })
   289→        }, 120000);
   290→
   291→        if (!response.ok) {
   292→            const errorMsg = await parseErrorResponse(response);
   293→            throw new Error(errorMsg);
   294→        }
   295→
   296→        const data = await response.json();
   297→        animatedCaptionsState.captions = data.captions;
   298→        animatedCaptionsState.keywords = data.keywords || [];
   299→
   300→        // Update UI
   301→        renderCaptionPreview();
   302→        updateCaptionStats(data);
   303→        showCaptionStatus(`Generated ${data.totalCaptions} caption lines`, 'success');
   304→
   305→    } catch (err) {
   306→        console.error('[SPLICE Captions] Generation error:', err);
   307→        showCaptionStatus(err.message, 'error');
   308→    } finally {
   309→        animatedCaptionsState.isGenerating = false;
   310→        updateGenerateButton(false);
   311→    }
   312→}
   313→
   314→function getLastTranscript() {
   315→    // Try main module state
   316→    if (window.spliceState?.lastTranscript) {
   317→        return window.spliceState.lastTranscript;
   318→    }
   319→    // Try local state
   320→    if (animatedCaptionsState.lastTranscript) {
   321→        return animatedCaptionsState.lastTranscript;
   322→    }
   323→    return null;
   324→}
   325→
   326→function setCaptionTranscript(transcript) {
   327→    animatedCaptionsState.lastTranscript = transcript;
   328→}
   329→
   330→// ============================================================================
   331→// CAPTION PREVIEW
   332→// ============================================================================
   333→function renderCaptionPreview() {
   334→    if (!captionsUI.previewContainer || !animatedCaptionsState.captions) return;
   335→
   336→    const captions = animatedCaptionsState.captions.slice(0, 10);
   337→    const fragment = document.createDocumentFragment();
   338→
   339→    const list = document.createElement('div');
   340→    list.className = 'caption-preview-list';
   341→
   342→    captions.forEach((caption, i) => {
   343→        const item = document.createElement('div');
   344→        item.className = 'caption-preview-item';
   345→        item.dataset.index = i;
   346→
   347→        const hasKeywords = caption.words?.some(w => w.isKeyword);
   348→        item.innerHTML = `
   349→            <span class="caption-time">${formatCaptionTime(caption.start)}</span>
   350→            <span class="caption-text ${hasKeywords ? 'has-keywords' : ''}">${formatCaptionText(caption)}</span>
   351→        `;
   352→
   353→        list.appendChild(item);
   354→    });
   355→
   356→    if (animatedCaptionsState.captions.length > 10) {
   357→        const more = document.createElement('div');
   358→        more.className = 'caption-preview-more';
   359→        more.textContent = `+ ${animatedCaptionsState.captions.length - 10} more captions`;
   360→        list.appendChild(more);
   361→    }
   362→
   363→    fragment.appendChild(list);
   364→
   365→    captionsUI.previewContainer.innerHTML = '';
   366→    captionsUI.previewContainer.appendChild(fragment);
   367→}
   368→
   369→function formatCaptionText(caption) {
   370→    if (!caption.words) return caption.text;
   371→
   372→    return caption.words.map(word => {
   373→        if (word.isKeyword) {
   374→            return `<span class="caption-keyword">${word.word}</span>`;
   375→        }
   376→        if (word.emoji) {
   377→            return `${word.word} ${word.emoji}`;
   378→        }
   379→        return word.word;
   380→    }).join(' ');
   381→}
   382→
   383→function formatCaptionTime(seconds) {
   384→    const mins = Math.floor(seconds / 60);
   385→    const secs = Math.floor(seconds % 60);
   386→    return `${mins}:${secs.toString().padStart(2, '0')}`;
   387→}
   388→
   389→function updateCaptionStats(data) {
   390→    if (!captionsUI.stats) return;
   391→
   392→    captionsUI.stats.innerHTML = `
   393→        <span class="stat-item">${data.totalCaptions} lines</span>
   394→        <span class="stat-item">${data.totalWords} words</span>
   395→        <span class="stat-item">${data.keywords?.length || 0} keywords</span>
   396→        <span class="stat-item">${formatCaptionTime(data.duration)}</span>
   397→    `;
   398→}
   399→
   400→// ============================================================================
   401→// EXPORT FUNCTIONS
   402→// ============================================================================
   403→async function exportCaptions(format) {
   404→    if (!animatedCaptionsState.captions) {
   405→        showCaptionStatus('No captions to export. Generate captions first.', 'error');
   406→        return;
   407→    }
   408→
   409→    showCaptionStatus(`Exporting ${format.toUpperCase()}...`, 'info');
   410→
   411→    try {
   412→        const apiUrl = getBackendUrl();
   413→        let endpoint, body;
   414→
   415→        if (format === 'mogrt') {
   416→            endpoint = '/captions/export/mogrt';
   417→            body = {
   418→                captions: animatedCaptionsState.captions,
   419→                settings: { frameRate: 30, width: 1920, height: 1080 }
   420→            };
   421→        } else {
   422→            endpoint = '/export/captions';
   423→            body = {
   424→                transcript: { words: flattenCaptionWords() },
   425→                format
   426→            };
   427→        }
   428→
   429→        const response = await fetchWithTimeout(`${apiUrl}${endpoint}`, {
   430→            method: 'POST',
   431→            headers: getAuthHeaders(),
   432→            body: JSON.stringify(body)
   433→        }, 60000);
   434→
   435→        if (!response.ok) {
   436→            const errorMsg = await parseErrorResponse(response);
   437→            throw new Error(errorMsg);
   438→        }
   439→
   440→        const data = await response.json();
   441→
   442→        if (format === 'mogrt') {
   443→            downloadJSON(data.data, 'animated-captions-mogrt.json');
   444→            showCaptionStatus('MOGRT data exported', 'success');
   445→        } else {
   446→            downloadText(data.content, `captions.${format}`);
   447→            showCaptionStatus(`${format.toUpperCase()} exported`, 'success');
   448→        }
   449→
   450→    } catch (err) {
   451→        console.error('[SPLICE Captions] Export error:', err);
   452→        showCaptionStatus(err.message, 'error');
   453→    }
   454→}
   455→
   456→function flattenCaptionWords() {
   457→    if (!animatedCaptionsState.captions) return [];
   458→
   459→    const words = [];
   460→    animatedCaptionsState.captions.forEach(caption => {
   461→        if (caption.words) {
   462→            caption.words.forEach(w => words.push(w));
   463→        }
   464→    });
   465→    return words;
   466→}
   467→
   468→async function copyCaptionsToClipboard() {
   469→    if (!animatedCaptionsState.captions) {
   470→        showCaptionStatus('No captions to copy', 'error');
   471→        return;
   472→    }
   473→
   474→    const text = animatedCaptionsState.captions
   475→        .map(c => `[${formatCaptionTime(c.start)}] ${c.text}`)
   476→        .join('\n');
   477→
   478→    try {
   479→        await navigator.clipboard.writeText(text);
   480→        showCaptionStatus('Captions copied to clipboard', 'success');
   481→    } catch (err) {
   482→        console.error('[SPLICE Captions] Copy failed:', err);
   483→        showCaptionStatus('Failed to copy', 'error');
   484→    }
   485→}
   486→
   487→// ============================================================================
   488→// TIMELINE INTEGRATION
   489→// ============================================================================
   490→async function applyCaptionsToTimeline() {
   491→    if (!animatedCaptionsState.captions) {
   492→        showCaptionStatus('No captions to apply', 'error');
   493→        return;
   494→    }
   495→
   496→    showCaptionStatus('Applying captions to timeline...', 'info');
   497→
   498→    try {
   499→        // Create markers for each caption
   500→        for (const caption of animatedCaptionsState.captions) {
   501→            const duration = caption.end - caption.start;
   502→            await jsx.call('createMarker', caption.start, caption.text, duration, null, 5); // CERULEAN color
   503→        }
   504→
   505→        showCaptionStatus(`Added ${animatedCaptionsState.captions.length} caption markers`, 'success');
   506→    } catch (err) {
   507→        console.error('[SPLICE Captions] Apply error:', err);
   508→        showCaptionStatus(err.message, 'error');
   509→    }
   510→}
   511→
   512→// ============================================================================
   513→// UI HELPERS
   514→// ============================================================================
   515→function showCaptionStatus(message, type = 'info') {
   516→    if (!captionsUI.status) return;
   517→
   518→    captionsUI.status.textContent = message;
   519→    captionsUI.status.className = `caption-status caption-status-${type}`;
   520→
   521→    // Auto-clear after 5 seconds for success/info
   522→    if (type !== 'error') {
   523→        setTimeout(() => {
   524→            captionsUI.status.textContent = '';
   525→            captionsUI.status.className = 'caption-status';
   526→        }, 5000);
   527→    }
   528→}
   529→
   530→function updateGenerateButton(isGenerating) {
   531→    if (!captionsUI.generateBtn) return;
   532→
   533→    captionsUI.generateBtn.disabled = isGenerating;
   534→    captionsUI.generateBtn.textContent = isGenerating ? 'Generating...' : 'Generate Captions';
   535→}
   536→
   537→function downloadJSON(data, filename) {
   538→    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
   539→    const url = URL.createObjectURL(blob);
   540→    const a = document.createElement('a');
   541→    a.href = url;
   542→    a.download = filename;
   543→    a.click();
   544→    URL.revokeObjectURL(url);
   545→}
   546→
   547→function downloadText(content, filename) {
   548→    const blob = new Blob([content], { type: 'text/plain' });
   549→    const url = URL.createObjectURL(blob);
   550→    const a = document.createElement('a');
   551→    a.href = url;
   552→    a.download = filename;
   553→    a.click();
   554→    URL.revokeObjectURL(url);
   555→}
   556→
   557→// ============================================================================
   558→// EXPORTS
   559→// ============================================================================
   560→if (typeof window !== 'undefined') {
   561→    window.initAnimatedCaptions = initAnimatedCaptions;
   562→    window.spliceAnimatedCaptions = {
   563→        init: initAnimatedCaptions,
   564→        generate: generateCaptions,
   565→        setTranscript: setCaptionTranscript,
   566→        selectTemplate: selectCaptionTemplate,
   567→        export: exportCaptions,
   568→        applyToTimeline: applyCaptionsToTimeline,
   569→        getState: () => animatedCaptionsState
   570→    };
   571→}
