   275→        minShotDuration: parseFloat(multitrackUI.minShotDuration?.value || 2),
   276→        wideShotPercentage: parseInt(multitrackUI.wideShotPercent?.value || 20),
   277→        switchingFrequency: parseInt(multitrackUI.switchingFrequency?.value || 50),
