   290→// ============================================================================
   291→// GET AUDIO PATH (CEP VERSION)
   292→// ============================================================================
   293→async function getAudioPath() {
   294→    try {
   295→        // Try to get audio path from active sequence
   296→        const seqInfo = await jsx.call('getActiveSequence');
   297→        if (seqInfo && seqInfo.audioPath) {
   298→            return seqInfo.audioPath;
   299→        }
   300→
   301→        // Fallback: export sequence audio for analysis
   302→        const exportResult = await jsx.call('exportSequenceAudioForAnalysis');
   303→        if (exportResult && exportResult.path) {
   304→            return exportResult.path;
   305→        }
   306→
   307→        // Last resort: get first clip audio path
   308→        const clipPath = await jsx.call('getFirstClipAudioPath');
   309→        if (clipPath) {
   310→            return clipPath;
   311→        }
   312→
   313→        throw new Error('Could not determine audio path');
   314→    } catch (e) {
   315→        console.error('[SPLICE Multitrack] Failed to get audio path:', e);
   316→        throw e;
   317→    }
   318→}
