   320→// ============================================================================
   321→// ANALYZE MULTITRACK
   322→// ============================================================================
   323→async function analyzeMultitrack() {
   324→    if (isMultitrackOperationInProgress) return;
   325→
   326→    // Check online status
   327→    if (typeof isOnline === 'function' && !isOnline()) {
   328→        setStatus('Offline - Check your connection');
   329→        return;
   330→    }
   331→
   332→    isMultitrackOperationInProgress = true;
   333→    if (multitrackUI.analyzeBtn) {
   334→        multitrackUI.analyzeBtn.disabled = true;
   335→        multitrackUI.analyzeBtn.textContent = 'Analyzing...';
   336→    }
   337→
   338→    try {
   339→        setStatus('Exporting audio tracks...');
   340→
   341→        // Get audio path
   342→        const audioPath = await getAudioPath();
   343→        if (!audioPath) {
   344→            throw new Error('Audio export path not initialized');
   345→        }
   346→
   347→        // For now, we use the main audio path
   348→        // In a full implementation, we'd export separate tracks per speaker
   349→        const audioPaths = [audioPath];
   350→
   351→        // Add additional tracks if multi-track sequence
   352→        if (speakerConfig.length > 1) {
   353→            for (let i = 1; i < speakerConfig.length; i++) {
   354→                audioPaths.push(audioPath);
   355→            }
   356→        }
   357→
   358→        setStatus('Analyzing speakers...');
   359→
   360→        const settings = getMultitrackSettings();
   361→        const apiUrl = getBackendUrl();
   362→
   363→        const response = await fetchWithTimeout(`${apiUrl}/multitrack`, {
   364→            method: 'POST',
   365→            headers: getAuthHeaders(),
   366→            body: JSON.stringify({
   367→                audioPaths,
   368→                speakerNames: settings.speakerNames,
   369→                videoTrackMapping: settings.videoTrackMapping,
   370→                wideShotEnabled: settings.wideShotEnabled,
   371→                wideShotPercentage: settings.wideShotPercentage,
   372→                wideShotTracks: settings.wideShotTracks,
   373→                minShotDuration: settings.minShotDuration,
   374→                switchingFrequency: settings.switchingFrequency
   375→            })
   376→        }, 120000);
   377→
   378→        if (!response.ok) {
   379→            const errorMsg = await parseErrorResponse(response);
   380→            throw new Error(errorMsg);
   381→        }
   382→
   383→        const data = await response.json();
   384→        analysisResults = data;
   385→
   386→        // Display results
   387→        displayMultitrackResults(data);
   388→
   389→        // Enable apply button
   390→        if (multitrackUI.applyBtn) {
   391→            multitrackUI.applyBtn.disabled = false;
   392→        }
   393→
   394→        setStatus(`Analysis complete - ${data.decisions?.length || 0} switching decisions`);
   395→
   396→    } catch (err) {
   397→        console.error('[SPLICE Multitrack] Analysis error:', err);
   398→        setStatus('Analysis failed: ' + err.message, true);
   399→    } finally {
   400→        isMultitrackOperationInProgress = false;
   401→        if (multitrackUI.analyzeBtn) {
   402→            multitrackUI.analyzeBtn.disabled = false;
   403→            multitrackUI.analyzeBtn.textContent = 'Analyze';
   404→        }
   405→    }
   406→}
   407→
   408→// ============================================================================
   409→// AUTO-BALANCE
   410→// ============================================================================
   411→async function autoBalanceMultitrack() {
   412→    if (isMultitrackOperationInProgress) return;
   413→
   414→    // Check online status
   415→    if (typeof isOnline === 'function' && !isOnline()) {
   416→        setStatus('Offline - Check your connection');
   417→        return;
   418→    }
   419→
   420→    isMultitrackOperationInProgress = true;
   421→    if (multitrackUI.autoBalanceBtn) {
