   506→
   507→function renderDistributionChart(metadata) {
   508→    if (!multitrackUI.distributionChart || !metadata) return;
   509→
   510→    const speakerPercentages = metadata.speakerPercentages || {};
   511→    const wideShotPct = metadata.wideShotPercentage || 0;
   512→
   513→    const fragment = document.createDocumentFragment();
   514→
   515→    // Add speaker bars
   516→    let speakerIndex = 0;
   517→    for (const [speaker, pct] of Object.entries(speakerPercentages)) {
