   533→        bar.style.width = `${wideShotPct}%`;
   534→        bar.textContent = wideShotPct >= 8 ? `Wide: ${wideShotPct.toFixed(0)}%` : '';
   535→        bar.title = `Wide Shot: ${wideShotPct.toFixed(1)}%`;
   536→        fragment.appendChild(bar);
