   541→}
   542→
   543→function renderSpeakerStats(metadata) {
   544→    if (!multitrackUI.speakerStats || !metadata) return;
   545→
   546→    const speakerPercentages = metadata.speakerPercentages || {};
   547→    const wideShotPct = metadata.wideShotPercentage || 0;
   548→
   549→    const fragment = document.createDocumentFragment();
   550→
   551→    // Add speaker stats
   552→    let speakerIndex = 0;
   553→    for (const [speaker, pct] of Object.entries(speakerPercentages)) {
   554→        const stat = document.createElement('div');
   555→        stat.className = 'speaker-stat';
