   586→
   587→function renderDecisionList(decisions) {
   588→    if (!multitrackUI.decisionList) return;
   589→
   590→    // Update count
   591→    if (multitrackUI.decisionCount) {
   592→        multitrackUI.decisionCount.textContent = decisions.length;
   593→    }
   594→
   595→    const fragment = document.createDocumentFragment();
   596→
   597→    // Limit to first 50 for performance
   598→    const displayDecisions = decisions.slice(0, 50);
   599→
   600→    displayDecisions.forEach((decision, i) => {
   601→        const item = document.createElement('div');
   602→        item.className = 'decision-item';
   603→
   604→        const speakerClass = decision.isWideShot ? 'wide-shot' : '';
   605→        const startTime = formatTime(decision.startTime);
   606→        const endTime = formatTime(decision.endTime);
   607→
   608→        item.innerHTML = `
   609→            <span class="decision-time">${startTime}-${endTime}</span>
   610→            <span class="decision-speaker ${speakerClass}">${decision.speakerName}</span>
   611→            <span class="decision-reason">${decision.reason || ''}</span>
   612→            <button class="decision-seek" data-time="${decision.startTime}" title="Seek to ${startTime}" aria-label="Seek to ${startTime}">&gt;</button>
   613→        `;
   614→
   615→        fragment.appendChild(item);
   616→    });
   617→
   618→    if (decisions.length > 50) {
   619→        const more = document.createElement('div');
   620→        more.className = 'decision-item';
   621→        more.style.justifyContent = 'center';
   622→        more.style.color = '#888';
   623→        more.textContent = `+ ${decisions.length - 50} more decisions`;
   624→        fragment.appendChild(more);
   625→    }
   626→
   627→    multitrackUI.decisionList.innerHTML = '';
   628→    multitrackUI.decisionList.appendChild(fragment);
