   630→
   631→function handleDecisionListClick(e) {
   632→    const seekBtn = e.target.closest('.decision-seek');
   633→    if (seekBtn) {
   634→        e.stopPropagation();
   635→        const time = parseFloat(seekBtn.dataset.time);
   636→        if (!isNaN(time)) {
   637→            seekToTime(time);
   638→        }
   639→    }
   640→}
