   642→// ============================================================================
   643→// SEEK TO TIME (CEP VERSION)
   644→// ============================================================================
   645→async function seekToTime(seconds) {
   646→    try {
   647→        await jsx.call('seekToTime', seconds);
   648→    } catch (e) {
   649→        console.warn('[SPLICE Multitrack] Failed to seek:', e);
   650→    }
   651→}
   652→
   653→// ============================================================================
   654→// APPLY MULTITRACK CUTS
   655→// ============================================================================
   656→async function applyMultitrackCuts() {
   657→    if (!analysisResults || !analysisResults.decisions) {
   658→        setStatus('No analysis data. Run analysis first.');
   659→        return;
   660→    }
   661→
   662→    if (isMultitrackOperationInProgress) return;
   663→
   664→    isMultitrackOperationInProgress = true;
   665→    if (multitrackUI.applyBtn) {
   666→        multitrackUI.applyBtn.disabled = true;
   667→        multitrackUI.applyBtn.textContent = 'Applying...';
   668→    }
   669→
   670→    try {
   671→        setStatus('Building multitrack sequence...');
   672→
   673→        // Check if builder is available
   674→        if (!window.spliceBuilder) {
   675→            throw new Error('Builder not initialized');
   676→        }
   677→
   678→        // Get active sequence info
   679→        const seqInfo = await jsx.call('getActiveSequence');
   680→        if (!seqInfo) {
   681→            throw new Error('No active sequence');
   682→        }
   683→
   684→        // Create cut list from decisions
   685→        const cutList = {
   686→            version: '3.5',
   687→            source: {
   688→                name: seqInfo.name,
   689→                path: seqInfo.treePath || seqInfo.name
   690→            },
   691→            segments: analysisResults.decisions.map(d => ({
   692→                type: d.isWideShot ? 'wide_shot' : (d.speakerIndex >= 0 ? 'speaker' : 'speech'),
   693→                sourceName: seqInfo.name,
   694→                sourcePath: seqInfo.treePath || seqInfo.name,
   695→                inPoint: d.startTime,
   696→                outPoint: d.endTime,
   697→                speaker: d.speakerName,
   698→                videoTrack: d.videoTrack,
   699→                colorHint: d.isWideShot ? 'yellow' :
   700→                           (d.speakerIndex === 0 ? 'mango' :
   701→                            d.speakerIndex === 1 ? 'caribbean' :
   702→                            d.speakerIndex === 2 ? 'lavender' : 'cerulean')
   703→            })),
   704→            metadata: {
   705→                isMultitrack: true,
   706→                speakerCount: speakerConfig.length,
   707→                decisionCount: analysisResults.decisions.length,
   708→                ...analysisResults.metadata
   709→            }
   710→        };
   711→
   712→        // Build sequence
   713→        const result = await window.spliceBuilder.buildSequenceFromCutList(cutList, {
   714→            suffix: '_MULTITRACK'
   715→        });
   716→
   717→        if (result.success) {
   718→            // Hide preview
   719→            if (multitrackUI.preview) {
   720→                multitrackUI.preview.classList.add('hidden');
   721→            }
   722→
   723→            setStatus(`Multitrack sequence created: ${result.stats?.clipsInserted || 0} clips`);
   724→
   725→            // Clear results
   726→            analysisResults = null;
   727→        } else {
   728→            throw new Error(result.error || 'Failed to build sequence');
   729→        }
   730→
   731→    } catch (err) {
   732→        console.error('[SPLICE Multitrack] Apply error:', err);
   733→        setStatus('Apply failed: ' + err.message, true);
   734→    } finally {
   735→        isMultitrackOperationInProgress = false;
   736→        if (multitrackUI.applyBtn) {
   737→            multitrackUI.applyBtn.disabled = true;
   738→            multitrackUI.applyBtn.textContent = 'Apply';
   739→        }
   740→    }
   741→}
   742→
   743→// ============================================================================
   744→// EXPORTS
   745→// ============================================================================
   746→// Expose for initialization from main.js
   747→if (typeof window !== 'undefined') {
   748→    window.initMultitrackUI = initMultitrackUI;
   749→    window.spliceMultitrack = {
   750→        init: initMultitrackUI,
   751→        analyze: analyzeMultitrack,
   752→        autoBalance: autoBalanceMultitrack,
   753→        apply: applyMultitrackCuts,
   754→        getSettings: getMultitrackSettings,
   755→        getResults: () => analysisResults
   756→    };
   757→}
