   365→function applyPreset(presetId) {
   366→    const preset = PRESETS[presetId];
   367→    if (!preset) return;
   368→
   369→    // Custom preset - keep current settings
   370→    if (presetId === 'custom' || !preset.settings) {
   371→        saveSettings({ activePreset: 'custom' });
   372→        setStatus('Using custom settings');
   373→        return;
   374→    }
   375→
   376→    const settings = preset.settings;
   377→
   378→    // Apply to UI
   379→    if (ui.sensitivitySlider && settings.sensitivity !== undefined) {
   380→        ui.sensitivitySlider.value = settings.sensitivity;
   381→        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   382→    }
   383→
   384→    if (ui.enableJCut) {
   385→        ui.enableJCut.checked = settings.enableJCut || false;
   386→        if (ui.jcutSettings) {
   387→            ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   388→        }
   389→    }
   390→
   391→    if (settings.jCutLeadIn !== undefined && ui.jcutLeadIn) {
   392→        ui.jcutLeadIn.value = Math.round(settings.jCutLeadIn * 100);
   393→        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jCutLeadIn + 's';
   394→    }
   395→
   396→    if (settings.jCutLeadOut !== undefined && ui.jcutLeadOut) {
   397→        ui.jcutLeadOut.value = Math.round(settings.jCutLeadOut * 100);
   398→        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jCutLeadOut + 's';
   399→    }
   400→
   401→    // Save merged settings
   402→    saveSettings({
   403→        activePreset: presetId,
   404→        sensitivity: settings.sensitivity,
   405→        threshold: settings.threshold,
   406→        minSilenceLength: settings.minSilenceLength,
   407→        paddingStart: settings.paddingStart,
   408→        paddingEnd: settings.paddingEnd,
   409→        enableTakesDetection: settings.enableTakesDetection,
   410→        enableJCut: settings.enableJCut || false,
   411→        jcutLeadIn: settings.jCutLeadIn || 0.3,
   412→        jcutLeadOut: settings.jCutLeadOut || 0.2
   413→    });
   414→
   415→    setStatus(`Applied ${preset.name} preset`);
   416→}
