    84→        const current = await redis.incr(key);
    85→
    86→        if (current === 1) {
    87→          // First request in window, set expiry
    88→          await redis.expire(key, windowSeconds);
    89→        }
    90→
    91→        const ttlValue = await redis.ttl(key);
    92→        const resetAt = Date.now() + (ttlValue * 1000);
    93→
