    95→          allowed: current <= max,
    96→          remaining: Math.max(0, max - current),
    97→          resetAt,
    98→          retryAfter: ttlValue
    99→        };
   100→      }
   101→    }
   102→  } catch (redisError) {
   103→    console.warn('[RateLimiter] Redis error, falling back to in-memory:', redisError.message);
   104→  }
   105→  
   106→  // Fallback to in-memory (single-instance only)
   107→  const now = Date.now();
   108→  const windowMs = windowSeconds * 1000;
   109→  const data = ipRequestCounts.get(key);
   110→  
   111→  if (!data || now - data.windowStart > windowMs) {
