   113→    ipRequestCounts.set(key, { count: 1, windowStart: now });
   114→    return {
   115→      allowed: true,
   116→      remaining: max - 1,
   117→      resetAt: now + windowMs,
   118→      retryAfter: windowSeconds
   119→    };
   120→  }
   121→  
   122→  data.count++;
   123→  const allowed = data.count <= max;
   124→  const remaining = Math.max(0, max - data.count);
   125→  const resetAt = data.windowStart + windowMs;
   126→  const retryAfter = Math.ceil((resetAt - now) / 1000);
   127→  
   128→  return {
   129→    allowed,
   130→    remaining,
   131→    resetAt,
   132→    retryAfter
   133→  };
   134→}
