   186→/**
   187→ * SECURITY: Endpoint-specific rate limiting
   188→ * Stricter limits for sensitive operations like authentication and file uploads
   189→ * @param {string} endpointKey - Key from RATE_LIMITS.endpoints
   190→ */
   191→function endpointRateLimit(endpointKey) {
   192→  return async (req, res, next) => {
   193→    const clientIp = req.ip || req.headers['x-forwarded-for']?.split(',')[0] || req.connection?.remoteAddress || 'unknown';
   194→    const limit = RATE_LIMITS.endpoints[endpointKey];
   195→    
   196→    if (!limit) {
