   199→    }
   200→    
   201→    const rateLimitKey = `ratelimit:endpoint:${endpointKey}:${clientIp}`;
   202→    
   203→    try {
   204→      const result = await checkRateLimit(rateLimitKey, limit.max, limit.window);
   205→      
   206→      // Add rate limit headers
   207→      res.set('X-RateLimit-Limit', limit.max.toString());
   208→      res.set('X-RateLimit-Remaining', result.remaining.toString());
