   234→// Rate limiting for legacy auth attempts (prevent brute force)
   235→const legacyAuthAttempts = new Map(); // IP -> { count, timestamp }
   236→const LEGACY_AUTH_WINDOW_MS = 60000; // 1 minute
   237→const LEGACY_AUTH_MAX_ATTEMPTS = 5; // 5 attempts per minute
