   321→            error: 'Invalid customer ID format',
   322→            message: 'Customer ID must be a valid Stripe customer ID'
   323→          });
   324→        }
   325→
   326→        // SECURITY: Verify customer exists in database before accepting
   327→        const exists = await customerExists(legacyId);
   328→        if (!exists) {
   329→          console.warn(`[SECURITY] Legacy auth attempt with non-existent customer: ${legacyId}`);
