   197â†’// ============================================================================
   198â†’// JSX CONNECTION CHECK
   199â†’// ============================================================================
   200â†’async function checkJSXConnection() {
   201â†’    try {
   202â†’        const result = await jsx.call('getVersion');
   203â†’        console.log('[SPLICE] JSX connection OK, version:', result);
   204â†’        return true;
   205â†’    } catch (e) {
   206â†’        console.warn('[SPLICE] JSX connection failed:', e.message);
   207â†’        setStatus('Premiere Pro connection failed', true);
   208â†’        return false;
   209â†’    }
   210â†’}
