   212â†’async function checkSequenceOpen() {
   213â†’    try {
   214â†’        const result = await jsx.call('checkSequenceOpen');
   215â†’        return result === true || result === 'true';
   216â†’    } catch {
   217â†’        return false;
   218â†’    }
