   221â†’// ============================================================================
   222â†’// OPTIONS TOGGLE
   223â†’// ============================================================================
   224â†’function initOptionsToggle() {
   225â†’    console.log('[SPLICE] initOptionsToggle - toggle:', !!ui.optionsToggle, 'panel:', !!ui.optionsPanel);
   226â†’    if (ui.optionsToggle && ui.optionsPanel) {
   227â†’        ui.optionsToggle.addEventListener('click', () => {
   228â†’            console.log('[SPLICE] Options toggle clicked');
   229â†’            // Check current state BEFORE toggling
   230â†’            const wasCollapsed = ui.optionsPanel.classList.contains('collapsed');
   231â†’            ui.optionsPanel.classList.toggle('collapsed');
   232â†’            // If it was collapsed, it's now expanded (show 'expanded' class)
   233â†’            ui.optionsToggle.classList.toggle('expanded', wasCollapsed);
   234â†’            saveSettings({ expandedOptions: wasCollapsed });
   235â†’        });
   236â†’
   237â†’        // Restore state
   238â†’        const settings = getSettings();
   239â†’        if (!settings.expandedOptions) {
   240â†’            ui.optionsPanel.classList.add('collapsed');
   241â†’            ui.optionsToggle.classList.remove('expanded');
   242â†’        } else {
   243â†’            ui.optionsToggle.classList.add('expanded');
   244â†’            ui.optionsPanel.classList.remove('collapsed');
   245â†’        }
   246â†’    }
   247â†’}
